import React, { Fragment } from 'react';
import PropTypes from 'prop-types';
import Moment from 'react-moment';
import LoadPickup from './LoadPickup';
import LoadDrop from './LoadDrop';
import { connect } from 'react-redux';
import { makeStyles, withStyles, useTheme  } from '@material-ui/core/styles';

import Paper from '@material-ui/core/Paper';
import Badge from '@material-ui/core/Badge';



import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import IconButton from '@material-ui/core/IconButton';
import Typography from '@material-ui/core/Typography';
import SkipPreviousIcon from '@material-ui/icons/SkipPrevious';
import PlayArrowIcon from '@material-ui/icons/PlayArrow';
import SkipNextIcon from '@material-ui/icons/SkipNext';
import ArrowForwardRoundedIcon from '@material-ui/icons/ArrowForwardRounded';

import AirplanemodeActive from '@material-ui/icons/AirplanemodeActive';
import VerticalTicketRip from '@mui-treasury/components/rip/verticalTicket';
import { useVerticalRipStyles } from '@mui-treasury/styles/rip/vertical';

import Grid from '@material-ui/core/Grid';

import theme from "../layout/ui/Theme";



const useStyles = makeStyles(({palette, breakpoints, theme}) => ({

  root: {
   display: 'flex',
   minWidth: 200,
   maxWidth: 300,
   marginBottom: 10,
   backgroundColor: "FFFFFF"

 },
 paper: {
     padding: theme.spacing(2),
     margin: 'auto',
     maxWidth: 500,
   },
   image: {
     width: 128,
     height: 128,
   },
   img: {
     margin: 'auto',
     display: 'block',
     maxWidth: '100%',
     maxHeight: '100%',
   },


}));


// <h5>
// {pickup.length > 0 ? (<Fragment>
//   {pickup.map(pickup => (
//     <LoadPickup key={pickup._id} pickup={pickup} />
//   ))}
// </Fragment>) : (<h4>No pickups listed</h4>)}
// </h5>

// <h5>
// {drop.length > 0 ? (<Fragment>
//   {drop.map(drop => (
//     <LoadDrop key={drop._id} drop={drop} />
//   ))}
// </Fragment>) : (<h4>No drops listed</h4>)}
// </h5>


const CurrentDayLoadItem = ({
  load: {
    user: { _id, name },
    brokerage,
    loadNumber,
    pickup,
    drop,
  },
}) => {
  const classes = useStyles();



  const pickupState = pickup.map((pickup) => pickup.pickupState);
  const pickupCity = pickup.map((pickup) => pickup.pickupCity);
  const pickupDate = pickup.map((pickup) => pickup.pickupDate);

  const dropState = drop.map((drop) => drop.dropState);
  const dropCity = drop.map((drop) => drop.dropCity);
  const dropDate = drop.map((drop) => drop.dropDate);

  const pickupStateFirst = pickupState[0];
  const pickupCityFirst = pickupCity[0];
  const pickDateFirst = pickupDate[0];

  const dropStateFirst = dropState[0];
  const dropCityFirst = dropCity[0];
  const dropDateFirst = dropDate[0];


  return (

    <div className={classes.root}>
  <Paper className={classes.paper}>
    <Grid container spacing={2}>
      <Grid item>
        <ButtonBase className={classes.image}>
          <img className={classes.img} alt="complex" src="/static/images/grid/complex.jpg" />
        </ButtonBase>
      </Grid>
      <Grid item xs={12} sm container>
        <Grid item xs container direction="column" spacing={2}>
          <Grid item xs>
            <Typography gutterBottom variant="subtitle1">
              Standard license
            </Typography>
            <Typography variant="body2" gutterBottom>
              Full resolution 1920x1080 • JPEG
            </Typography>
            <Typography variant="body2" color="textSecondary">
              ID: 1030114
            </Typography>
          </Grid>
          <Grid item>
            <Typography variant="body2" style={{ cursor: 'pointer' }}>
              Remove
            </Typography>
          </Grid>
        </Grid>
        <Grid item>
          <Typography variant="subtitle1">$19.00</Typography>
        </Grid>
      </Grid>
    </Grid>
  </Paper>
</div>

  );
};




CurrentDayLoadItem.propTypes = {
  load: PropTypes.object.isRequired,
};




export default CurrentDayLoadItem;
