import React, { useState, useEffect } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { makeStyles, useTheme } from '@material-ui/core/styles';

import { Paper, withStyles, Grid, TextField, Button, FormControlLabel, Checkbox } from '@material-ui/core';
import { Face, Fingerprint } from '@material-ui/icons';

import { Link, withRouter } from 'react-router-dom';
import { createProfile, getCurrentProfile } from '../../actions/profile';



const useStyles = makeStyles((theme) => ({

  margin: {

  },
  padding: {

  }

}));


const EditProfile = ({ profile: { profile, loading }, createProfile, getCurrentProfile, history}) => {

  const classes = useStyles();
  const theme = useTheme();

  const [formData, setFormData] = useState({
    company: '',
    title: ''
  });

  const {
    company,
    title
  } = formData;

  useEffect(() => {
    getCurrentProfile();

    setFormData({
      company: loading || !profile.company ? '' : profile.company,
      title: loading || !profile.title ? '' : profile.title

    });

  }, [loading]);

  const onChange = e =>
  setFormData({ ...formData, [e.target.name]: e.target.value });

  const onSubmit = e => {
    e.preventDefault();
    createProfile(formData, history, true);
  };

  return (

    <Grid  container style={{ marginTop: '150px' }} justify="center" alignItems="center">
        <form onSubmit={e => onSubmit(e)}>
           <Grid container spacing={1} justify="center" alignItems="center" >
               <Grid item style={{ marginTop: '20px' }} >
                   <Face />
               </Grid>
               <Grid item >
                   <TextField id="company" name="company"  type="text" label="Company" value={company} onChange={e => onChange(e)} />
               </Grid>
           </Grid>
           <Grid container spacing={1}  style={{ marginTop: '10px' }}justify="center" alignItems="center">
               <Grid item style={{ marginTop: '20px' }}>
                   <Fingerprint />
               </Grid>
               <Grid item >
                   <TextField id="title" name="title" label="Title" type="text" value={title} onChange={e => onChange(e)} />
               </Grid>
           </Grid>
           <Grid container alignItems="center" justify="center" style={{ marginTop: '20px' }}>
               <Button type="submit" variant="outlined" color="primary" style={{ textTransform: "none" }}>Update</Button>
               <Button button component={Link} to="/dashboard" variant="outlined" color="primary" style={{ textTransform: "none", marginLeft: '10px' }}>Back</Button>
           </Grid>
              </form>
           </Grid>


  );
};

EditProfile.propTypes = {
  createProfile: PropTypes.func.isRequired,
  getCurrentProfile: PropTypes.func.isRequired,
  profile: PropTypes.object.isRequired
};

const mapStateToProps = state => ({
  profile: state.profile

});

export default connect(mapStateToProps, { createProfile, getCurrentProfile })(withRouter(EditProfile));
