import React, { useEffect, useState } from "react";
// Redux
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { addDriver } from "../../actions/driver.js";
import { getLoads } from "../../actions/load";
//Material
import Fab from "@material-ui/core/Fab";
import AddIcon from "@material-ui/icons/Add";
import Dialog from "@material-ui/core/Dialog";
import DialogActions from "@material-ui/core/DialogActions";
import DialogContent from "@material-ui/core/DialogContent";
import DialogTitle from "@material-ui/core/DialogTitle";
import Grid from "@material-ui/core/Grid";
import TextField from "@material-ui/core/TextField";
import Button from "@material-ui/core/Button";
import Select from "@material-ui/core/Select";
import InputLabel from "@material-ui/core/InputLabel";
import Input from "@material-ui/core/Input";
import Chip from "@material-ui/core/Chip";
import MenuItem from "@material-ui/core/MenuItem";
import FormControl from '@material-ui/core/FormControl';
import { makeStyles, useTheme } from "@material-ui/core/styles";

const useStyles = makeStyles((theme) => ({
  backButton: {
    marginRight: theme.spacing(1),
  },
  form: {
    display: "flex",
    flexDirection: "column",
    margin: "auto",
    width: "fit-content",
  },
  formControl: {
    margin: theme.spacing(1),
    minWidth: 120,
    marginTop: 0
  },
  chips: {
    display: "flex",
    flexWrap: "wrap",
  },
  chip: {
    margin: 2,
  },
  noLabel: {
    marginTop: theme.spacing(3),
  },
}));

const ITEM_HEIGHT = 48;
const ITEM_PADDING_TOP = 8;
const MenuProps = {
  PaperProps: {
    style: {
      maxHeight: ITEM_HEIGHT * 4.5 + ITEM_PADDING_TOP,
      width: 350,
    },
  },
};

function getStyles(name, loads, theme) {
  return {
    fontWeight: loads.indexOf(name) === -1 ? theme.typography.fontWeightRegular : theme.typography.fontWeightMedium,
  };
}

const isPhoneNumber = (input) => {
  var phoneno = /^\(?([0-9]{3})\)?[-]?([0-9]{3})[-]?([0-9]{4})$/;
  if (input.match(phoneno)) {
    return true;
  } else {
    return false;
  }
};

const AddDriverForm = ({ user, addDriver, all_drivers, getLoads, load: { loads, allLoads, loading } }) => {
  const classes = useStyles();
  const theme = useTheme();

  const formTemplate = {
    firstName: "",
    lastName: "",
    phoneNumber: "",
    loads: [],
    user: ""
  };

  const [open, setOpen] = useState(false);
  const [count, setCount] = useState(1);
  const [form, setForm] = useState(formTemplate);

  useEffect(() => {
    if (count == 2) {
      getLoads();
    }
  }, [count]);

  const handleClickOpen = () => {
    // setForm(formTemplate);
    setCount(1);
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
    // setForm(formTemplate);
    setCount(1);
  };

  const handleNext = () => {
    if (count === 1) {
      if (!isPhoneNumber(form.phoneNumber)) {
        alert("Please Submit a phone number in this format: XXX-XXX-XXXX");
        return;
      }
    }
    setCount((prevActiveStep) => prevActiveStep + 1);
  };

  const handleBack = () => {
    setCount((prevActiveStep) => prevActiveStep - 1);
  };

  const onSubmit = (e) => {
    e.preventDefault();

    addDriver(form);

    handleClose();
  };

  const updateForm = (e) => {
    setForm({
      ...form,
      [e.target.name]: e.target.value,
    });
  };

  const handleLoadsChange = (e) => {
    setForm({
      ...form,
      loads: [...e.target.value],
    });
  };
  return (
    <>
      { (user && user.role === 'afterhours') || <Fab color="primary" onClick={handleClickOpen}>
        <AddIcon />
      </Fab> }

      <Dialog fullWidth={true} maxWidth={"sm"} open={open} onClose={handleClose} aria-labelledby="form-dialog-title">
        <DialogTitle id="form-dialog-title">Add Driver</DialogTitle>
        <DialogContent>
          <div className="">
            <form className={classes.form}>
              {count === 1 ? (
                <div>
                  <div className="form-group">
                    <TextField
                      id="outlined-basic"
                      type="firstName"
                      className="form-control"
                      name="firstName"
                      label="First Name"
                      onChange={(e) => updateForm(e)}
                      value={form.firstName}
                      style={{ marginRight: "10px" }}
                    />

                    <TextField
                      id="outlined-basic"
                      type="lastName"
                      className="form-control"
                      name="lastName"
                      label="Last Name"
                      onChange={(e) => updateForm(e)}
                      value={form.lastName}
                    />
                  </div>
                  <div className="form-group">
                    <TextField
                      id="outlined-basic"
                      type="phoneNumber"
                      className="form-control"
                      name="phoneNumber"
                      label="Phone Number"
                      onChange={(e) => updateForm(e)}
                      value={form.phoneNumber}
                    />
                    <FormControl className={classes.formControl}>
                      <InputLabel id="demo-simple-select-label">Select Driver</InputLabel>
                      <Select
                        labelId="demo-simple-select-label"
                        id="demo-simple-select"
                        value={form.user}
                        name="user"
                        onChange={(e) => updateForm(e)}
                      >
                        {all_drivers.map((driver) => (
                          <MenuItem key={driver._id} value={driver._id}>
                            { driver.email }
                          </MenuItem>
                        ))}
                      </Select>
                    </FormControl>
                  </div>
                </div>
              ) : null}

              {count === 2 ? (
                <div className="form-group">
                  <InputLabel id="assignedLoadsLabel">Assigned Loads</InputLabel>
                  <Select
                    labelId="assignedLoadsLabel"
                    id="assignedLoads"
                    multiple
                    value={form.loads}
                    style={{ width: "350px" }}
                    onChange={handleLoadsChange}
                    input={<Input id="select-multiple-chip" />}
                    renderValue={(selected) => (
                      <div className={classes.chips}>
                        {selected.map((value) => (
                          <Chip key={value._id} label={value.loadNumber} className={classes.chip} />
                        ))}
                      </div>
                    )}
                    MenuProps={MenuProps}
                  >
                    {allLoads.map((load) => (
                      <MenuItem key={load._id} value={load} style={getStyles(load, loads, theme)}>
                        Load Number: {load.loadNumber}, Number of Pickups: {load.pickup.length}
                      </MenuItem>
                    ))}
                  </Select>
                </div>
              ) : null}
            </form>
            <Grid container spacing={1} style={{ marginTop: "20px" }}>
              <Grid item xs={12}>
                <Button className="" onClick={onSubmit}>
                  Submit
                </Button>
              </Grid>
              <Grid item xs={6}>
                <Button className="" onClick={handleBack} disabled={count === 1}>
                  Back
                </Button>
              </Grid>
              <Grid item xs={6}>
                <Button className="" onClick={handleNext} disabled={count > 1}>
                  Next
                </Button>
              </Grid>
            </Grid>
          </div>
        </DialogContent>
      </Dialog>
    </>
  );
};

AddDriverForm.propTypes = {
  addDriver: PropTypes.func.isRequired,
  getLoads: PropTypes.func.isRequired,
  load: PropTypes.object.isRequired,
};

const mapStateToProps = ({ load, auth: { user }, driver: { all_drivers } }) => ({ load, user, all_drivers });

export default connect(mapStateToProps, { addDriver, getLoads })(AddDriverForm);
