import React, { Fragment, useState } from "react";
import PropTypes from "prop-types";
import Moment from "react-moment";
import LoadPickup from "./LoadPickup";
import LoadDrop from "./LoadDrop";
import { connect } from "react-redux";
import { makeStyles, withStyles, useTheme } from "@material-ui/core/styles";

import Paper from "@material-ui/core/Paper";
import Badge from "@material-ui/core/Badge";
import { TablePagination } from "@material-ui/core";
import IconButton from "@material-ui/core/IconButton";
import Typography from "@material-ui/core/Typography";
import SkipPreviousIcon from "@material-ui/icons/SkipPrevious";
import PlayArrowIcon from "@material-ui/icons/PlayArrow";
import SkipNextIcon from "@material-ui/icons/SkipNext";
import ArrowForwardRoundedIcon from "@material-ui/icons/ArrowForwardRounded";
import EditIcon from "@material-ui/icons/Edit";
import DoneIcon from "@material-ui/icons/Done";
import CloseIcon from "@material-ui/icons/Close";
import SettingsOverscanIcon from "@material-ui/icons/SettingsOverscan";
import "date-fns";
import DateFnsUtils from "@date-io/date-fns";
import { MuiPickersUtilsProvider, KeyboardTimePicker, KeyboardDatePicker, DatePicker, TimePicker } from "@material-ui/pickers";
import Dialog from "@material-ui/core/Dialog";
import DialogContent from "@material-ui/core/DialogContent";
import DialogContentText from "@material-ui/core/DialogContentText";
import FormControl from "@material-ui/core/FormControl";

import TextField from "@material-ui/core/TextField";

import Grid from "@material-ui/core/Grid";

import theme from "../layout/ui/Theme";
import { SportsRugbySharp } from "@material-ui/icons";

import { patchPickup, patchDrop, getLoads } from "../../actions/load";

const useStyles = makeStyles((theme) => ({
  root: {
    display: "flex",
    minWidth: 200,
    maxWidth: 350,
    marginBottom: 10,
    backgroundColor: "#FFFFFF",
    borderRadius: 5,
    boxShadow: "1px 3px 1px #9E9E9E",
  },
  cardcontent: {
    flexDirection: "row",
    wrap: "wrap",
    minWidth: 100,
    maxWidth: 120,
    paddingLeft: 20,
    paddingTop: 15,
    paddingBottom: 5,
    "&:last-child": {
      paddingBottom: 5,
    },
  },
  pickup: {
    fontSize: 12,
    alignItems: "center",
  },
  h4: {
    margin: 0,
    padding: 0,
  },
  loadp: {
    display: "flex",
    margin: 0,
    padding: 0,
    paddingLeft: 150,
    alignItems: "center",
  },
  playIcon: {
    height: 38,
    width: 30,
    marginLeft: 0,
  },
  textField: {
    "& input": {
      color: "#000000",
    },
    "& input:disabled": {
      color: "#000000",
    },
  },
  textFieldDialog: {
    "& input": {
      color: "#000000",
    },
    "& input:disabled": {
      color: "#000000",
    },
  },
  textFieldDialogPickup: {
    "& input": {
      color: "#000000",
    },
    "& input:disabled": {
      color: "#000000",
    },
    marginLeft: "75px"
  },
  textFieldDialogDrop: {
    "& input": {
      color: "#000000",
    },
    "& input:disabled": {
      color: "#000000",
    },
    marginLeft: "25px"
  },
  resize: {
    fontSize: 12,
  },
  resizeDialog: {
    fontSize: 14,
  },
}));

const CurrentDayLoadItem = ({
  load: {
    user: { _id, name },
    brokerage,
    loadNumber,
    pickup,
    drop,
  },
  patchPickup,
  patchDrop,
  getLoads,
  load_id,
}) => {
  const classes = useStyles();

  const [open, setOpen] = useState(false);

  const [editing, setEditing] = useState(false);

  const [firstPickup, setFirstPickup] = useState(pickup[0]);
  const copyFirstPickup = {...pickup[0]};

  const [firstDrop, setFirstDrop] = useState(drop[0]);

  const currentDate = new Date();

  const pickupDateFormat = new Date(copyFirstPickup.pickupDate);


  const match =
    currentDate.getMonth() === pickupDateFormat.getMonth() &&
    currentDate.getDay() === pickupDateFormat.getDay() &&
    currentDate.getFullYear() === pickupDateFormat.getFullYear();

  const handlePatch = async () => {
    setEditing(false);
    await patchPickup(load_id, firstPickup);
    await patchDrop(load_id, firstDrop);
    getLoads();
  };

  const handleOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
    setEditing(false);
  };

  const handleUndoPatch = () => {
    setEditing(false);
    setFirstPickup(pickup[0]);
  };

  const handleEditOn = () => {
    setEditing(true);
  };

  const updatePickup = (e) => {
    if (e.target.name === "pickupZip" && !isNaN(parseInt(e.target.value))) {
      setFirstPickup({
        ...firstPickup,
        [e.target.name]: parseInt(e.target.value),
      });
    } else if (e.target.name !== "pickupZip") {
      setFirstPickup({
        ...firstPickup,
        [e.target.name]: e.target.value,
      });
    };
  };

  const updateDrop = (e) => {
    if (e.target.name === "dropZip" && !isNaN(parseInt(e.target.value))) {
      setFirstDrop({
        ...firstDrop,
        [e.target.name]: parseInt(e.target.value),
      });
    } else if (e.target.name !== "dropZip") {
      setFirstDrop({
        ...firstDrop,
        [e.target.name]: e.target.value,
      });
    }
  };

  const updateDate = (date, i) => {
    // i = 0 for pickUp, 1 for dropOff
    if (i === 0) {
      setFirstPickup({
        ...firstPickup,
        ["pickupDate"]: date,
      });
    } else if (i === 1) {
      setFirstDrop({
        ...firstDrop,
        ["dropDate"]: date,
      });
    }
  };

  return (
    <div>
      {match ? (
        <>
          <Grid container className={classes.root}>
            <Grid item wrap="nowrap" className={classes.cardcontent}>
              <Typography className={classes.pickup}>
                <h4 className={classes.h4}>Pickup</h4>
                <TextField
                  type="pickupCity"
                  name="pickupCity"
                  className={classes.textField}
                  InputProps={{
                    disableUnderline: true,
                    classes: { input: classes.resize },
                  }}
                  disabled={true}
                  size="small"
                  value={firstPickup.pickupCity}
                />
                <TextField
                  type="pickupState"
                  name="pickupState"
                  className={classes.textField}
                  InputProps={{
                    disableUnderline: true,
                    classes: { input: classes.resize },
                  }}
                  disabled={true}
                  size="small"
                  value={firstPickup.pickupState}
                />
                <MuiPickersUtilsProvider utils={DateFnsUtils}>
                  <TimePicker
                    className={classes.textField}
                    InputProps={{
                      disableUnderline: true,
                      classes: { input: classes.resize },
                    }}
                    disabled={true}
                    size="small"
                    id="time-picker"
                    value={firstPickup.pickupDate}
                  />
                  <DatePicker
                    className={classes.textField}
                    InputProps={{
                      disableUnderline: true,
                      classes: { input: classes.resize },
                    }}
                    disabled={true}
                    size="small"
                    id="date-picker-dialog"
                    format="MM/dd/yyyy"
                    value={firstPickup.pickupDate}
                  />
                </MuiPickersUtilsProvider>
              </Typography>
            </Grid>

            {/* <Grid item className={classes.cardcontent}>
      <Typography   className={classes.pickup}>
      <p className={classes.p}>{loadNumber}</p>
      <IconButton><ArrowForwardRoundedIcon className={classes.playIcon} /></IconButton>
      <p className={classes.p}>Johnson</p>
      </Typography>
      </Grid> */}

            <Grid item className={classes.cardcontent}>
              <Typography className={classes.pickup}>
                <h4 className={classes.h4}>Drop</h4>
                <TextField
                  type="dropCity"
                  name="dropCity"
                  className={classes.textField}
