import React, { useState } from "react";
import Button from "@material-ui/core/Button";
import TextField from "@material-ui/core/TextField";
import Dialog from "@material-ui/core/Dialog";
import DialogActions from "@material-ui/core/DialogActions";
import DialogContent from "@material-ui/core/DialogContent";
import DialogContentText from "@material-ui/core/DialogContentText";
import DialogTitle from "@material-ui/core/DialogTitle";
import Fab from "@material-ui/core/Fab";
import AddIcon from "@material-ui/icons/Add";

import { Link, withRouter } from "react-router-dom";
import { useForm } from "react-hook-form";
import * as yup from "yup";

import LoadForm from "./LoadCompany";
import LoadPickup from "./LoadPickup";

import { makeStyles, useTheme } from "@material-ui/core/styles";
import Stepper from "@material-ui/core/Stepper";
import Step from "@material-ui/core/Step";
import StepLabel from "@material-ui/core/StepLabel";
import Typography from "@material-ui/core/Typography";

import PropTypes from "prop-types";
import { connect } from "react-redux";
import { addLoad } from "../../actions/load";

const useStyles = makeStyles((theme) => ({
  backButton: {
    marginRight: theme.spacing(1),
  },
  instructions: {
    marginTop: theme.spacing(1),
    marginBottom: theme.spacing(1),
  },
}));

const StepperForm = ({ addLoad }) => {
  //Create Schema to validate load Details

  const loadSchema = yup.object().shape({
    brokerage: yup.string().required("Company Name is required"),
    loadNumber: yup.string().required("Load Number is required"),
  });

  //Create Schema to validate load pickup Details

  const loadPickupSchema = yup.object().shape({
    shipperName: yup.string().required("Shipper Name is required"),
  });

  // Initialise and set Validation the hook to use for the load details form
  const loadForm = useForm({
    validationSchema: loadSchema,
  });

  // Initialise and set Validation the hook to use for the load pickup form
  const loadPickup = useForm({
    validationSchema: loadPickupSchema,
  });

  const [text, setText] = useState("");
  const [open, setOpen] = React.useState(false);

  const handleClickOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  function getSteps() {
    return ["Load Details", "Load Pickup Details", "Load Drop Details"];
  }

  function getStepContent(stepIndex) {
    switch (stepIndex) {
      case 0:
        return <LoadForm formProps={loadForm} />;
      case 1:
        return <LoadPickup formProps={loadPickup} />;
      case 2:
        return "hello3";
      default:
        return "Done";
    }
  }

  const classes = useStyles();
  const theme = useTheme();
  const [activeStep, setActiveStep] = React.useState(0);
  const steps = getSteps();

  const handleNext = () => {
    setActiveStep((prevActiveStep) => prevActiveStep + 1);
  };

  const handleBack = () => {
    setActiveStep((prevActiveStep) => prevActiveStep - 1);
  };

  const handleReset = () => {
    setActiveStep(0);
  };

  //add load data for load details, pickup, and drop

  const [formData, setFormData] = useState({
    brokerage: "",
    loadNumber: "",
    shipperName: ""
  });

  const { brokerage, loadNumber, shipperName } = formData;

  const onChange = (e) =>
    setFormData({ ...formData, [e.target.name]: e.target.value });

  const onSubmit = (e) => {
    e.preventDefault();
    addLoad(formData);
  };

  return (
    <div>
      <Fab color="primary" onClick={handleClickOpen}>
        <AddIcon />
      </Fab>

      <Dialog
        open={open}
        onClose={handleClose}
        aria-labelledby="form-dialog-title"
      >
        <DialogContent>
        <form onSubmit={ activeStep === 0 ? LoadForm.handleSubmit(onSubmit) : LoadPickup.handleSubmit(onSubmit)}>
          <Stepper activeStep={activeStep} alternativeLabel>
            {steps.map((label) => (
              <Step key={label}>
                <StepLabel>{label}</StepLabel>
              </Step>
            ))}
          </Stepper>
          </form>
        </DialogContent>

        <DialogActions>
          <div>
            {activeStep === steps.length ? (
              <div>
                <Typography className={classes.instructions}>
                  Load Added
                </Typography>
                <Button onClick={handleReset}>Add another Load</Button>
              </div>
            ) : (
              <div>
                <div>
                  <Button
                    disabled={activeStep === 0}
                    onClick={handleBack}
                    className={classes.backButton}
                  >
                    Back
                  </Button>
                  <Button
                    variant="contained"
                    color="primary"
                    onClick={handleNext}
                  >
                    {activeStep === steps.length - 1 ? "Submit" : "Next"}
                  </Button>
                </div>
              </div>
            )}
          </div>
        </DialogActions>
      </Dialog>
    </div>
  );
};

StepperForm.propTypes = {
  addLoad: PropTypes.func.isRequired,
};

export default connect(null, { addLoad })(withRouter(StepperForm));
