import React, { Fragment } from 'react';
import PropTypes from 'prop-types';
import Moment from 'react-moment';
import LoadPickup from './LoadPickup';
import LoadDrop from './LoadDrop';
import { connect } from 'react-redux';
import { makeStyles, withStyles, useTheme  } from '@material-ui/core/styles';

import Paper from '@material-ui/core/Paper';
import Badge from '@material-ui/core/Badge';

import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import IconButton from '@material-ui/core/IconButton';
import Typography from '@material-ui/core/Typography';
import SkipPreviousIcon from '@material-ui/icons/SkipPrevious';
import PlayArrowIcon from '@material-ui/icons/PlayArrow';
import SkipNextIcon from '@material-ui/icons/SkipNext';
import ArrowForwardRoundedIcon from '@material-ui/icons/ArrowForwardRounded';

import theme from "../layout/ui/Theme";

const useStyles = makeStyles((theme) => ({

  root: {
   display: 'flex',
   minWidth: 300
 },
 details: {
   display: 'flex',
   flexDirection: 'column',
 },
 content: {
   flex: '1 0 auto',
 },
 controls: {
   display: 'flex',
   alignItems: 'center',
   paddingLeft: theme.spacing(1),
   paddingBottom: theme.spacing(1),
 },
 pickup: {
   fontSize: 14,
 },
 p: {
   margin:0,
   padding:0
 },
 playIcon: {
   height: 38,
   width: 30,
   marginLeft: 0
 },

}));


// <h5>
// {pickup.length > 0 ? (<Fragment>
//   {pickup.map(pickup => (
//     <LoadPickup key={pickup._id} pickup={pickup} />
//   ))}
// </Fragment>) : (<h4>No pickups listed</h4>)}
// </h5>

// <h5>
// {drop.length > 0 ? (<Fragment>
//   {drop.map(drop => (
//     <LoadDrop key={drop._id} drop={drop} />
//   ))}
// </Fragment>) : (<h4>No drops listed</h4>)}
// </h5>


const CurrentDayLoadItem = ({
  load: {
    user: { _id, name },
    brokerage,
    loadNumber,
    pickup,
    drop,
  },
}) => {
  const classes = useStyles();
  const theme = useTheme();

  const pickupState = pickup.map((pickup) => pickup.pickupState);
  const pickupCity = pickup.map((pickup) => pickup.pickupCity);
  const pickupDate = pickup.map((pickup) => pickup.pickupDate);

  const dropState = drop.map((drop) => drop.dropState);
  const dropCity = drop.map((drop) => drop.dropCity);
  const dropDate = drop.map((drop) => drop.dropDate);

  const pickupStateFirst = pickupState[0];
  const pickupCityFirst = pickupCity[0];
  const pickDateFirst = pickupDate[0];

  const dropStateFirst = dropState[0];
  const dropCityFirst = dropCity[0];
  const dropDateFirst = dropDate[0];


  return (
    <Card zeroMinWidth className={classes.root}>
      <div className={classes.details}>
        <div className={classes.controls}>
        <CardContent className={classes.content}>
         <Typography className={classes.pickup}>
         <p className={classes.p}>Pickup</p>
         <p className={classes.p}>{pickupStateFirst}</p>
         <p className={classes.p}>0800 EST</p>
         <p className={classes.p}>3/11</p>
         </Typography >
          </CardContent>
          <Typography className={classes.pickup}>
          <p className={classes.p}>2664578</p>
          <IconButton><ArrowForwardRoundedIcon className={classes.playIcon} /></IconButton>
          <p className={classes.p}>Johnson</p>
          </Typography>
          <CardContent className={classes.content}>
          <Typography className={classes.pickup}>
            <p className={classes.p}>Drop</p>
            <p className={classes.p}>{dropCityFirst}, {dropStateFirst}</p>
            <p className={classes.p}>1200 EST</p>
            <p className={classes.p}>3/11</p>
          </Typography>
          </CardContent>
        </div>
      </div>
    </Card>

  );
};




CurrentDayLoadItem.propTypes = {
  load: PropTypes.object.isRequired,
};




export default CurrentDayLoadItem;
