import React, { Fragment } from 'react';
import PropTypes from 'prop-types';
import Moment from 'react-moment';
import LoadPickup from './LoadPickup';
import LoadDrop from './LoadDrop';
import { connect } from 'react-redux';
import { makeStyles, withStyles } from '@material-ui/core/styles';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';
import Badge from '@material-ui/core/Badge';


// <h5>
// {pickup.length > 0 ? (<Fragment>
//   {pickup.map(pickup => (
//     <LoadPickup key={pickup._id} pickup={pickup} />
//   ))}
// </Fragment>) : (<h4>No pickups listed</h4>)}
// </h5>

// <h5>
// {drop.length > 0 ? (<Fragment>
//   {drop.map(drop => (
//     <LoadDrop key={drop._id} drop={drop} />
//   ))}
// </Fragment>) : (<h4>No drops listed</h4>)}
// </h5>

const useStyles = makeStyles({
  table: {
    minWidth: 750,
  },
});

const StyledBadge = withStyles((theme) => ({
  badge: {
    right: -7,
    top: 0,
    border: `2px solid ${theme.palette.background.paper}`,
    padding: "0 4px",
  },
}))(Badge);

const LoadItem = ({
  load: {
    user: { _id, name },
    brokerage,
    loadNumber,
    pickup,
    drop,
  },
}) => {
  const classes = useStyles();

  const pickupState = pickup.map((pickup) => pickup.pickupState);
  const pickupCity = pickup.map((pickup) => pickup.pickupCity);
  const pickupDate = pickup.map((pickup) => pickup.pickupDate);

  const dropState = drop.map((drop) => drop.dropState);
  const dropCity = drop.map((drop) => drop.dropCity);
  const dropDate = drop.map((drop) => drop.dropDate);

  const pickupStateFirst = pickupState[0];
  const pickupCityFirst = pickupCity[0];
  const pickDateFirst = pickupDate[0];

  const dropStateFirst = dropState[0];
  const dropCityFirst = dropCity[0];
  const dropDateFirst = dropDate[0];


  return (
    <TableContainer component={Paper}>
      <Table aria-label="caption table">
        <caption>A basic table example with a caption</caption>
        <TableHead>
          <TableRow>
            <TableCell align="right">Load Number</TableCell>
            <TableCell>Pickup</TableCell>
            <TableCell>Pick Date</TableCell>
            <TableCell>Drop</TableCell>
            <TableCell>Drop Date</TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          <TableRow>
            <TableCell>{loadNumber}</TableCell>
            {pickup.length <= 2 ? (
              <Fragment>
                <TableCell>{pickupCityFirst, pickupStateFirst}</TableCell>
              </Fragment>
            ) : (
              <TableCell>
                <StyledBadge color="primary" badgeContent={pickup.length}>
                  {pickupCityFirst}, {pickupStateFirst}
                </StyledBadge>
              </TableCell>
            )}
            <TableCell>
            <Moment format='MM/DD'>{pickDateFirst}</Moment>
            </TableCell>
            <TableCell>
            {dropCityFirst}, {dropStateFirst}
            </TableCell>
            <TableCell>
            <Moment format='MM/DD'>{dropDateFirst}</Moment>
            </TableCell>
          </TableRow>
        </TableBody>
      </Table>
    </TableContainer>
  );
};




LoadItem.propTypes = {
  load: PropTypes.object.isRequired,
};




export default LoadItem;
