import axios from "axios";
import { setAlert } from "./alert";

import { GET_LOADS, GET_LOAD, LOAD_ERROR, ADD_LOAD, PATCH_PICKUP, PICKUP_ERROR, PATCH_DROP, DROP_ERROR, DELETE_LOAD, DELETE_LOAD_ERROR } from "./types";

// Get current users loads
export const getLoads = (page, limit) => async (dispatch) => {
  try {
    const url = `/api/load/me?page=${page+1}&limit=${limit}`;
    const res = await axios.get(url);
    dispatch({
      type: GET_LOADS,
      payload: { loads: res.data, page, limit },
    });
  } catch (err) {
    dispatch({
      type: LOAD_ERROR,
      payload: { msg: err, status: err },
    });
  }
};

// Get post
export const getLoad = (id) => async (dispatch) => {
  try {
    const res = await axios.get(`/loads/${id}`);

    dispatch({
      type: GET_LOAD,
      payload: res.data,
    });
  } catch (err) {
    dispatch({
      type: LOAD_ERROR,
      payload: { msg: err.response.statusText, status: err.response.status },
    });
  }
};

//add load
export const addLoad = (formData) => async (dispatch) => {
  try {
    const config = {
      headers: {
        "Content-Type": "application/json",
      },
    };
    console.log('=========================  LOADINF...............');
    const res = await axios.post("/api/load", formData, config);

    dispatch({
      type: ADD_LOAD,
      payload: res.data,
    });

    dispatch(setAlert("Load Created", "success"));
  } catch (err) {
    dispatch({
      type: LOAD_ERROR,
      payload: { msg: err.response.statusText, status: err.response.status },
    });
  }
};

//add pickup

//patch pickup
export const patchPickup = (load_id, pickup) => async (dispatch) => {
  try {
    const res = await axios.patch(`/api/load/pickup?load_id=${load_id}`, pickup);
    dispatch({
      type: PATCH_PICKUP,
      payload: res.data,
    });
  } catch (err) {
    dispatch({
      type: PICKUP_ERROR,
      payload: { msg: err.response.statusText, status: err.response.status },
    });
  }
};

//patch drop
export const patchDrop = (load_id, drop) => async (dispatch) => {
  try {
    const res = await axios.patch(`/api/load/drop?load_id=${load_id}`, drop);
    dispatch({
      type: PATCH_DROP,
      payload: res.date,
    });
  } catch (err) {
    dispatch({
      type: DROP_ERROR,
      payload: { msg: err.response.statusText, status: err.response.status },
    });
  }
};

// Delete a load
export const deleteLoad = (load_id) => async (dispatch) => {
  try {
    const res = await axios.delete(`/api/load`, { data: { load_id: load_id } });
    console.log("RES:                 ", res);
    dispatch({
      type: DELETE_LOAD,
      payload: load_id,
    });
  } catch (err) {
    dispatch({
      type: DELETE_LOAD_ERROR,
      payload: { msg: err },
    });
  }
};
