import {
  GET_LOADS,
  LOAD_ERROR
} from '../actions/types';

const initialState = {
  loads: [],
  load: null,
  loading: true,
  error: {}
};

export default function(state = initialState, action) {
  const { type, payload } = action;

  switch(type) {
    case GET_LOADS:
      return {
        ...state,
        loads: payload,
        loading: false
      };
      case GET_LOAD:
        return {
          ...state,
          load: payload,
          loading: false
        };
      case LOAD_ERROR:
        return {
          ...state,
          error: payload,
          loading: false
      };
      default:
        return state;
  }

}
