import axios from 'axios';
import {setAlert} from './alert';

import {
  GET_LOADS,
  GET_LOAD,
  LOAD_ERROR
} from './types';

// Get current users loads
export const getLoads = () => async dispatch => {
  try {
    const res = await axios.get('/api/load/me');
    dispatch({
      type: GET_LOADS,
      payload: res.data
    });
  } catch (err) {
    dispatch({
      type: LOAD_ERROR,
      payload: { msg: err.response.statusText, status: err.response.status}
    });
  }
};

// Get post
export const getLoad = id => async dispatch => {
  try {
    const res = await axios.get(`/loads/${id}`);

    dispatch({
      type: GET_LOAD,
      payload: res.data
    });
  } catch (err) {
    dispatch({
      type: LOAD_ERROR,
      payload: { msg: err.response.statusText, status: err.response.status }
    });
  }
};
