import React, { Fragment, useState, useEffect } from "react";
import { BrowserRouter as Router, Link } from "react-router-dom";
import AppBar from "@material-ui/core/AppBar";
import Toolbar from "@material-ui/core/Toolbar";
import Typography from "@material-ui/core/Typography";
import useScrollTrigger from "@material-ui/core/useScrollTrigger";
import { makeStyles } from "@material-ui/core/styles";
import Tabs from "@material-ui/core/Tabs";
import Tab from "@material-ui/core/Tab";
import useMediaQuery from "@material-ui/core/useMediaQuery";
import SwipeableDrawer from "@material-ui/core/SwipeableDrawer";
import IconButton from "@material-ui/core/IconButton";
import MenuIcon from "@material-ui/icons/Menu";
import List from "@material-ui/core/List";
import ListItem from "@material-ui/core/ListItem";
import ListItemText from "@material-ui/core/ListItemText";

import theme from "./ui/Theme";

import { connect } from "react-redux";
import PropTypes from "prop-types";
import { logout } from "../../actions/auth";

function ElevationScroll(props) {
  const { children } = props;

  const trigger = useScrollTrigger({
    disableHysteresis: true,
    threshold: 0
  });

  return React.cloneElement(children, {
    elevation: trigger ? 4 : 0
  });
}

const useStyles = makeStyles(theme => ({
  toolbarMargin: {
    ...theme.mixins.toolbar
  },
  tabContainer: {
    marginLeft: "auto"
  },

  tab: {
    ...theme.typography.tab,
    minWidth: 10,
    marginLeft: "15px"
  },

  drawerIconContainer: {
    marginLeft: "auto",
    "&:hover": {
      backgroundColor: "transparent"
    }
  },
  drawerIcon: {
    color: "#1891FC",
    height: "30px",
    width: "30px"
  },
  drawerItem: {
    ...theme.typography.tab,
    color: "#1891FC",
    opacity: 0.7
  },
  drawerItemSelected: {
    "& .MuiListItemText-root": {
        opacity: 1
    }
  },
  appbar: {
    zIndex: theme.zIndex.modal + 1,
  }
}));

const Navbar = ({ auth: { isAuthenticated, loading } }) => {
  const classes = useStyles();
  const iOS = process.browser && /iPad|iPhone|iPod/.test(navigator.userAgent);
  const matches = useMediaQuery(theme.breakpoints.down("md"));

  const [openDrawer, setOpenDrawer] = useState(false);

  const [value, setValue] = useState(0);



  const handleChange = (e, value) => {
    setValue(value);
  };


  useEffect(() => {
    switch (window.location.pathname) {
      case "/":
        setValue(0);
      break;
      case "/about":
        setValue(1);
      break;
      case "/contact":
        setValue(2);
      break;
      case "/login":
        setValue(3);
      break;
      default:
      break;
    }
  }, []);



  const tabs = (
    <React.Fragment>
      <Tabs
        value={value}
        onChange={handleChange}
        className={classes.tabContainer}
      >
        <Tab className={classes.tab} component={Link} to="/" label="home" />
        <Tab className={classes.tab} component={Link} label="about" />
        <Tab className={classes.tab} component={Link} label="contact" />
        <Tab
          className={classes.tab}
          component={Link}
          to="/login"
          label="login"
        />
      </Tabs>
    </React.Fragment>
  );

  const drawer = (
    <React.Fragment>
      <SwipeableDrawer
        disableBackdropTransition={!iOS}
        disableDiscovery={iOS}
        open={openDrawer}
        onClose={() => setOpenDrawer(false)}
        onOpen={() => setOpenDrawer(true)}
      >
      <div className={classes.toolbarMargin} />
        <List disablePadding>
          <ListItem
            onClick={() => {
              setOpenDrawer(false);
              setValue(0);
            }}
            button
            component={Link}
            to="/"
            selected={value === 0}
            classes={{selected: classes.drawerItemSelected}}
          >
            <ListItemText
              className={classes.drawerItem}
              disableTypography
            >
              home
            </ListItemText>
          </ListItem>
          <ListItem
            onClick={() => {
              setOpenDrawer(false);
              setValue(1);
            }}
            button
            component={Link}
            selected={value === 1}
            classes={{selected: classes.drawerItemSelected}}
          >
            <ListItemText className={
              classes.drawerItem}
              disableTypography>
              about
            </ListItemText>
          </ListItem>
          <ListItem
            onClick={() => {
              setOpenDrawer(false);
              setValue(2);
            }}
            button
            component={Link}
            selected={value === 2}
            classes={{selected: classes.drawerItemSelected}}
          >
            <ListItemText className={
                classes.drawerItem
            } disableTypography>
              contact
            </ListItemText>
          </ListItem>
          <ListItem
            onClick={() => {
              setOpenDrawer(false);
              setValue(3);
            }}
            button
            component={Link}
            to="/login"
            selected={value === 3}
            classes={{selected: classes.drawerItemSelected}}
          >
            <ListItemText className={
                classes.drawerItem
            } disableTypography>
              login
            </ListItemText>
          </ListItem>
        </List>
      </SwipeableDrawer>
      <IconButton
        className={classes.drawerIconContainer}
        onClick={() => setOpenDrawer(!openDrawer)}
        disableRipple
      >
        <MenuIcon className={classes.drawerIcon} />
      </IconButton>
    </React.Fragment>
  );

  return (
    <React.Fragment>
      <ElevationScroll>
        <AppBar position="fixed" color="secondary" className={classes.appbar}>
          <Toolbar disablegutter>
            <Typography className={classes.tab}>freightdok.</Typography>
            {matches ? drawer : tabs}
          </Toolbar>
        </AppBar>
      </ElevationScroll>
      <div className={classes.toolbarMargin} />
    </React.Fragment>
  );
};


Navbar.propTypes = {
  auth: PropTypes.object.isRequired
};

const mapStateToProps = state => ({
  auth: state.auth
});

export default connect(
  mapStateToProps,
  { logout }
)(Navbar);
