import React, { Fragment, useState } from "react";
import PropTypes from "prop-types";
import Moment from "react-moment";
import LoadPickup from "./LoadPickup";
import LoadDrop from "./LoadDrop";
import { connect } from "react-redux";
import { makeStyles, withStyles, useTheme } from "@material-ui/core/styles";

import Paper from "@material-ui/core/Paper";
import Badge from "@material-ui/core/Badge";
import { TablePagination } from "@material-ui/core";
import IconButton from "@material-ui/core/IconButton";
import Typography from "@material-ui/core/Typography";
import SkipPreviousIcon from "@material-ui/icons/SkipPrevious";
import PlayArrowIcon from "@material-ui/icons/PlayArrow";
import SkipNextIcon from "@material-ui/icons/SkipNext";
import ArrowForwardRoundedIcon from "@material-ui/icons/ArrowForwardRounded";
import EditIcon from "@material-ui/icons/Edit";
import DoneIcon from "@material-ui/icons/Done";
import CloseIcon from "@material-ui/icons/Close";
import ArrowForward from "@material-ui/icons/ArrowForward";
import SettingsOverscanIcon from "@material-ui/icons/SettingsOverscan";
import "date-fns";
import DateFnsUtils from "@date-io/date-fns";
import { MuiPickersUtilsProvider, KeyboardTimePicker, KeyboardDatePicker, DatePicker, TimePicker } from "@material-ui/pickers";
import Dialog from "@material-ui/core/Dialog";
import DialogContent from "@material-ui/core/DialogContent";
import DialogContentText from "@material-ui/core/DialogContentText";
import FormControl from "@material-ui/core/FormControl";

import TextField from "@material-ui/core/TextField";

import Grid from "@material-ui/core/Grid";

import theme from "../layout/ui/Theme";
import { SportsRugbySharp } from "@material-ui/icons";

import { patchPickup, patchDrop, getLoads } from "../../actions/load";
import LoadDetailModal from "./LoadDetailModal";

const useStyles = makeStyles((theme) => ({
  root: {
    // display: "flex",
    minWidth: 200,
    maxWidth: 350,
    marginBottom: 10,
    backgroundColor: "#FFFFFF",
    borderRadius: 5,
    borderWidth: 1,
    borderStyle: 'solid',
    borderColor: 'grey',
    // boxShadow: "1px 3px 1px #9E9E9E",
  },
  cardcontent: {
    flexDirection: "row",
    wrap: "wrap",
    minWidth: 100,
    maxWidth: 150,
    paddingLeft: 20,
    paddingTop: 15,
    paddingBottom: 5,
    "&:last-child": {
      paddingBottom: 5,
    },
  },
  pickup: {
    fontSize: 12,
    alignItems: "center",
  },
  h4: {
    margin: 0,
    padding: 0,
  },
  loadp: {
    display: "flex",
    margin: 0,
    padding: 0,
    paddingLeft: 150,
    alignItems: "center",
  },
  playIcon: {
    height: 38,
    width: 30,
    marginLeft: 0,
  },
  textField: {
    "& input": {
      color: "#000000",
    },
    "& input:disabled": {
      color: "#000000",
    },
  },
  textFieldDialog: {
    "& input": {
      color: "#000000",
    },
    "& input:disabled": {
      color: "#000000",
    },
  },
  textFieldDialogPickup: {
    "& input": {
      color: "#000000",
    },
    "& input:disabled": {
      color: "#000000",
    },
    marginLeft: "75px"
  },
  textFieldDialogDrop: {
    "& input": {
      color: "#000000",
    },
    "& input:disabled": {
      color: "#000000",
    },
    marginLeft: "25px"
  },
  resize: {
    fontSize: 12,
  },
  resizeDialog: {
    fontSize: 14,
  },

  newLoad: {
    margin: 0
  }
}));

const CurrentDayLoadItem = ({
  load,
  patchPickup,
  patchDrop,
  getLoads,
  load_id,
}) => {
  const classes = useStyles();
  const {
    user: { _id, name },
    brokerage,
    loadNumber,
    pickup,
    drop,
  } = load;
  const [open, setOpen] = useState(false);

  const [editing, setEditing] = useState(false);

  const [firstPickup, setFirstPickup] = useState(pickup[0]);
  const copyFirstPickup = {...pickup[0]};

  const [firstDrop, setFirstDrop] = useState(drop[0]);

  const currentDate = new Date();

  const pickupDateFormat = new Date(copyFirstPickup.pickupDate);


  const match =
    currentDate.getMonth() === pickupDateFormat.getMonth() &&
    currentDate.getDate() === pickupDateFormat.getDate() &&
    currentDate.getFullYear() === pickupDateFormat.getFullYear(); 

  const handlePatch = async () => {
    setEditing(false);
    await patchPickup(load_id, firstPickup);
    await patchDrop(load_id, firstDrop);
    getLoads();
  };

  const handleOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
    setEditing(false);
  };

  const handleUndoPatch = () => {
    setEditing(false);
    setFirstPickup(pickup[0]);
  };

  const handleEditOn = () => {
    setEditing(true);
  };

  const updatePickup = (e) => {
    if (e.target.name === "pickupZip" && !isNaN(parseInt(e.target.value))) {
      setFirstPickup({
        ...firstPickup,
        [e.target.name]: parseInt(e.target.value),
      });
    } else if (e.target.name !== "pickupZip") {
      setFirstPickup({
        ...firstPickup,
        [e.target.name]: e.target.value,
      });
    };
  };

  const updateDrop = (e) => {
    if (e.target.name === "dropZip" && !isNaN(parseInt(e.target.value))) {
      setFirstDrop({
        ...firstDrop,
        [e.target.name]: parseInt(e.target.value),
      });
    } else if (e.target.name !== "dropZip") {
      setFirstDrop({
        ...firstDrop,
        [e.target.name]: e.target.value,
      });
    }
  };

  const updateDate = (date, i) => {
    // i = 0 for pickUp, 1 for dropOff
    if (i === 0) {
      setFirstPickup({
        ...firstPickup,
        ["pickupDate"]: date,
      });
    } else if (i === 1) {
      setFirstDrop({
        ...firstDrop,
        ["dropDate"]: date,
      });
    }
  };

  return (
    <div>
      {match ? (
        <>
          {/* <div className={classes.root} onClick={handleOpen}>

              <div style={{marginBottom: 10, textAlign: 'center', margin: 0}}>
                <p style={{marginTop: 5, marginBottom: 2, fontSize: 18}}>Load {load.loadNumber ? load.loadNumber : ''}</p>
              </div>

              <div style={{display: 'flex'}}>
                <Grid item wrap="nowrap" className={classes.cardcontent} style={{paddingTop: 0}}>
                    <h2 className={classes.h4}>Pickup</h2>
                    <p className={classes.newLoad}>{pickup[0] ? pickup[0].pickupCity : ''}</p>
                    <p className={classes.newLoad}>{pickup[0] ? pickup[0].pickupState : ''}</p>
                  <Typography className={classes.pickup}>
                    <MuiPickersUtilsProvider utils={DateFnsUtils}>
                      <TimePicker
                        className={classes.textField}
                        InputProps={{
                          disableUnderline: true,
                          classes: { input: classes.resize },
                        }}
                        disabled={true}
                        size="small"
                        id="time-picker"
                        value={pickup[0] ? pickup[0].pickupDate : ''}
                      />
                      <DatePicker
                        className={classes.textField}
                        InputProps={{
                          disableUnderline: true,
                          classes: { input: classes.resize },
                        }}
                        disabled={true}
                        size="small"
                        id="date-picker-dialog"
                        format="MM/dd/yyyy"
                        value={pickup[0] ? pickup[0].pickupDate : ''}
                      />
                    </MuiPickersUtilsProvider>
                  </Typography>
                </Grid>

                <ArrowForward style={{color: 'black', marginTop: '13%'}} />

                <Grid item className={classes.cardcontent} style={{paddingTop: 0}}>
                  <h2 className={classes.h4}>Drop</h2>
                  <p className={classes.newLoad}>{drop[0] ? drop[0].dropCity : ''}</p>
                  <p className={classes.newLoad}>{drop[0] ? drop[0].dropState : ''}</p>
                  <Typography className={classes.pickup}>
                    <MuiPickersUtilsProvider utils={DateFnsUtils}>
                      <TimePicker
                        className={classes.textField}
                        InputProps={{
                          disableUnderline: true,
                          classes: { input: classes.resize },
                        }}
                        disabled={true}
                        size="small"
                        id="time-picker"
                        value={drop[0] ? drop[0].dropDate : ''}
                      />
                      <DatePicker
                        className={classes.textField}
                        InputProps={{
                          disableUnderline: true,
                          classes: { input: classes.resize },
                        }}
                        disabled={true}
                        size="small"
                        id="date-picker-dialog"
                        format="MM/dd/yyyy"
                        value={drop[0] ? drop[0].dropDate : ''}
                      />
                    </MuiPickersUtilsProvider>
                  </Typography>
                </Grid>
              </div>


          </div> */}

          <LoadDetailModal 
          open={open}
          load={load} 
          handleClose={()=>{setOpen(false)}} />
          <Dialog fullWidth={true} maxWidth={"md"} open={false} onClose={handleClose}>
            <DialogContent>
              <Grid container justify="space-around" direction="row" alignItems="center">
                <Grid item xs={6} alignItems="center">
                  <TextField
                    className={classes.textFieldDialog}
                    id="standard-full-width"
                    fullWidth
                    InputProps={{
                      disableUnderline: true,
                    }}
                    disabled={true}
                    value={`Load Number: ${loadNumber}`}
                  />
                </Grid>
                <Grid item xs={6}>
                  <IconButton style={{ float: "right" }} onClick={handleClose}>
                    <CloseIcon />
                  </IconButton>
                </Grid>
                <Grid item xs={6} alignItems="center" style={{ marginTop: "50px" }}>
                  <TextField
                    className={classes.textFieldDialog}
                    id="standard-full-width"
                    fullWidth
                    InputProps={{
                      disableUnderline: true,
                      style: { fontWeight: "bold" },
                    }}
                    style={{ marginLeft: "75px" }}
                    disabled={true}
                    value={"Pickup"}
                  />
                </Grid>
                <Grid item xs={6} alignItems="center" style={{ marginTop: "50px" }}>
                  <TextField
                    className={classes.textFieldDialog}
                    id="standard-full-width"
                    fullWidth
                    InputProps={{
                      disableUnderline: true,
                      style: { fontWeight: "bold" },
                    }}
                    style={{ marginLeft: "25px" }}
                    disabled={true}
                    value={"Drop"}
                  />
                </Grid>
                <Grid item xs={6} alignItems="center">
                  <TextField
                    type="shipperName"
                    name="shipperName"
                    className={classes.textFieldDialogPickup}
                    InputProps={{
                      disableUnderline: !editing,
                      classes: { input: classes.resizeDialog },
                    }}
                    disabled={!editing}
                    size="medium"
                    value={firstPickup.shipperName}
                    onChange={updatePickup}
                  />
                </Grid>
                <Grid item xs={6} alignItems="center">
                  <TextField
                    type="receiverName"
                    name="receiverName"
                    className={classes.textFieldDialogDrop}
                    InputProps={{
                      disableUnderline: !editing,
                      classes: { input: classes.resizeDialog },
                      style: {  },
                    }}
                    disabled={!editing}
                    size="medium"
                    value={firstDrop.receiverName}
                    onChange={updateDrop}
                  />
                </Grid>
                <Grid item xs={6} alignItems="center">
                  <TextField
                    type="pickupAddress"
                    name="pickupAddress"
                    className={classes.textFieldDialogPickup}
                    InputProps={{
                      disableUnderline: !editing,
                      classes: { input: classes.resizeDialog },
                      style: {  },
                    }}
                    disabled={!editing}
                    size="medium"
                    value={firstPickup.pickupAddress}
                    onChange={updatePickup}
                  />
                </Grid>
                <Grid item xs={6} alignItems="center">
                  <TextField
                    type="dropAddress"
                    name="dropAddress"
                    className={classes.textFieldDialogDrop}
                    InputProps={{
                      disableUnderline: !editing,
                      classes: { input: classes.resizeDialog },
                    }}
                    disabled={!editing}
                    size="medium"
                    value={firstDrop.dropAddress}
                    onChange={updateDrop}
                  />
                </Grid>
                <Grid item xs={6} alignItems="center">
                  {!editing && (
                    <TextField
                      type="pickupCityStateZip"
                      name="pickupCityStateZip"
                      className={classes.textFieldDialogPickup}
                      InputProps={{
                        disableUnderline: true,
                        classes: { input: classes.resizeDialog },
                      }}
                      style={{ minWidth: "250px" }}
                      disabled={true}
                      size="medium"
                      value={`${firstPickup.pickupCity}, ${firstPickup.pickupState}, ${firstPickup.pickupZip}`}
                    />
                  )}
                  {editing && (
                    <Grid container style={{ marginLeft: "75px" }}>
                      <Grid item xs={3}>
                        <TextField
                          type="pickupCity"
                          name="pickupCity"
                          className={classes.textFieldDialog}
                          InputProps={{
                            disableUnderline: !editing,
                            classes: { input: classes.resizeDialog },
                          }}
                          disabled={!editing}
                          size="medium"
                          value={firstPickup.pickupCity}
                          onChange={updatePickup}
                        />
                      </Grid>
                      <Grid item xs={3}>
                        <TextField
                          wrap="wrap"
                          type="pickupState"
                          name="pickupState"
                          className={classes.textFieldDialog}
                          InputProps={{
                            disableUnderline: !editing,
                            classes: { input: classes.resizeDialog },
                          }}
                          disabled={!editing}
                          size="medium"
                          value={firstPickup.pickupState}
                          onChange={updatePickup}
                        />
                      </Grid>
                      <Grid item xs={3}>
                        <TextField
                          type="pickupZip"
                          name="pickupZip"
                          className={classes.textFieldDialog}
                          InputProps={{
                            disableUnderline: !editing,
                            classes: { input: classes.resizeDialog },
                          }}
                          disabled={!editing}
                          size="medium"
                          value={firstPickup.pickupZip}
                          onChange={updatePickup}
                        />
                      </Grid>
                      <Grid item xs={3} />
                    </Grid>
                  )}
                </Grid>
                <Grid item xs={6} alignItems="center">
                  {!editing && (
                    <TextField
                      type="dropCityStateZip"
                      name="dropCityStateZip"
                      className={classes.textFieldDialogDrop}
                      InputProps={{
                        disableUnderline: true,
                        classes: { input: classes.resizeDialog },
                      }}
                      style={{ minWidth: "250px" }}
                      disabled={true}
                      size="medium"
                      value={`${firstDrop.dropCity}, ${firstDrop.dropState}, ${firstDrop.dropZip}`}
                    />
                  )}
                  {editing && (
                    <Grid container style={{ marginLeft: "25px" }}>
                      <Grid item xs={3}>
                        <TextField
                          type="dropCity"
                          name="dropCity"
                          className={classes.textFieldDialog}
                          InputProps={{
                            disableUnderline: !editing,
                            classes: { input: classes.resizeDialog },
                          }}
