import React, {useState} from 'react';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import DialogTitle from '@material-ui/core/DialogTitle';
import Fab from '@material-ui/core/Fab';
import AddIcon from '@material-ui/icons/Add';
import 'date-fns';
import DateFnsUtils from '@date-io/date-fns';
import {
  MuiPickersUtilsProvider,
  KeyboardTimePicker,
  KeyboardDatePicker,
} from '@material-ui/pickers';
import Grid from '@material-ui/core/Grid';

import LoadForm  from './LoadNumber';

import { makeStyles } from '@material-ui/core/styles';
import Typography from '@material-ui/core/Typography';

import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { addLoad } from '../../actions/load';

const useStyles = makeStyles((theme) => ({
  backButton: {
    marginRight: theme.spacing(1),
  },
  instructions: {
    marginTop: theme.spacing(1),
    marginBottom: theme.spacing(1),
  },
  form: {
    display: 'flex',
    flexDirection: 'column',
    margin: 'auto',
    width: 'fit-content',
  }
}));


const AddLoadForm = ({ addLoad }) => {
  const [text, setText] = useState('');
  const [open, setOpen] = React.useState(false);

  const classes = useStyles();

  const formTemplate = {
    brokerage:'',
    loadNumber:'',
    pickUp:[],
    dropOff:[],
  }

  const pickUpTemplate = {
    shipperName:'',
    pickupAddress:'',
    pickupCity:'',
    pickupState:'',
    pickupZip:'',
    pickupDate: new Date(),
    pickupPo:'',
    pickupDeliverNumber:'',
    pickupReference:'',
  }

  const dropOffTemplate = {
    receiverName:'',
    dropAddress:'',
    dropCity:'',
    dropState:'',
    dropZip:'',
    dropDate: new Date(),
    dropPo:'',
    dropDeliverNumber:'',
    dropReference:'',
  }

  const handleClickOpen = () => {
    setForm(formTemplate);
    setCount(1);
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
    setForm(formTemplate);
    setCount(1);
  };

  const [count, setCount] = React.useState(1);

  const handleNext = () => {
      setCount(prevActiveStep => prevActiveStep + 1);
  };

  const handleBack = () => {
      setCount(prevActiveStep => prevActiveStep - 1);
  };

  const[form, setForm] = useState(formTemplate);



  const[pickUp, setPickup ] = useState(pickUpTemplate);

  const[dropOff, setDropOff ] = useState(dropOffTemplate);

  const handleNewPickDrop = () => {
    const tempForm = {...form};

    tempForm.pickUp.push({...pickUp});
    tempForm.dropOff.push({...dropOff});

    setForm({...tempForm});

    setPickup(pickUpTemplate);
    setDropOff(dropOffTemplate);

    setCount(2);

    console.log(form);

  }


  const {
    brokerage,
    loadNumber,

  } = form;



  const updateForm = e =>{
    setForm({
      ...form,
      [e.target.name]: e.target.value
    });
  }

  const updatePickUp = e =>{
    if( e.target.name === "pickupZip" && !isNaN(parseInt(e.target.value)) ){
      setPickup({
        ...pickUp,
        [e.target.name]: parseInt(e.target.value)
      });

    }else if(e.target.name !== "pickupZip"){
      console.log(e.target.value)
      setPickup({
        ...pickUp,
        [e.target.name]: e.target.value
      });
    }

  }

  const updateDate = (date,i) => {
    // i = 0 for pickUp, 1 for dropOff
    if(i === 0){
      setPickup({
        ...pickUp,
        ['pickupDate']: date
      });
    }else if(i===1){
      setDropOff({
        ...dropOff,
        ['dropDate']: date
      });
    }
  }

  const updateDropOff = e =>{
    if( e.target.name === "dropZip" && !isNaN(parseInt(e.target.value))){
      setDropOff({
        ...dropOff,
        [e.target.name]: parseInt(e.target.value)
      });
    }else if(e.target.name !== "dropZip"){
      setDropOff({
        ...dropOff,
        [e.target.name]: e.target.value
      })
    }

  }


    const onSubmit = e => {
      e.preventDefault();

      handleNewPickDrop();

      addLoad(form);

      handleClose();

    };




  return (
    <div>

      <Fab color="primary" onClick={handleClickOpen}>
        <AddIcon />
      </Fab>

      <Dialog fullWidth={true} maxWidth={'sm'} open={open} onClose={handleClose} aria-labelledby="form-dialog-title">

        <DialogTitle id="form-dialog-title">Add Load</DialogTitle>

        <DialogContent>

        <div className="">
        <form className={classes.form}>

        { count === 1 ? (
        <div>
        <div className="form-group">
        <TextField id="outlined-basic" type="brokerage" className="form-control" name="brokerage" label="Brokerage" onChange={e => updateForm(e)} value={brokerage} />
        </div>

        <div className="form-group">
        <TextField id="outlined-basic" type="loadNumber" className="form-control" name="loadNumber" label="Load Number" onChange={e => updateForm(e)} value={loadNumber} />
        </div>
        </div>

        ) : null }

        { count === 2 ? (
        <div>
        <div className="form-group">
        <TextField id="outlined-basic" type="shipperName" className="form-control" name="shipperName" label="Shipper Name" onChange={e => updatePickUp(e)} value={pickUp.shipperName}  />
        </div>

        <div className="form-group">
        <TextField id="outlined-basic" type="receiverName" className="form-control" name="receiverName" label="Receiver Name" onChange={e => updateDropOff(e)} value={dropOff.receiverName} />
        </div>

        </div>

        ) : null }

        {/* TODO: put these in a grid to fix spacing */}
        {count === 3 ? (
          <div>
            <div className="form-group">
              <Grid container spacing={3}>
                <Grid item xs>
                  <TextField id="outlined-basic" type="pickupAddress" className="form-control" name="pickupAddress" label="Pickup Address" onChange={e=>updatePickUp(e)} value={pickUp.pickupAddress}/>
                </Grid>
                <Grid item xs>
                  <TextField id="outlined-basic" type="pickupCity" className="form-control" name="pickupCity" label="Pickup City" onChange={e=>updatePickUp(e)} value={pickUp.pickupCity}/>  
                </Grid>
                <Grid item xs>
                  <TextField id="outlined-basic" type="pickupState" className="form-control" name="pickupState" label="Pickup State" onChange={e=>updatePickUp(e)} value={pickUp.pickupState}/>
                </Grid>
                <Grid item xs>
                  <TextField id="outlined-basic" type="pickupZip" className="form-control" name="pickupZip" label="Pickup Zip" onChange={e=>updatePickUp(e)} value={pickUp.pickupZip}/>
                </Grid>
              </Grid>
            </div>

            <div className="form-group">
              <Grid container spacing={3}>
                <Grid item xs>
                  <TextField id="outlined-basic" type="dropAddress" className="form-control" name="dropAddress" label="Dropoff Address" onChange={e=>updateDropOff(e)} value={dropOff.dropAddress}/>
                </Grid>
                <Grid item xs>
                  <TextField id="outlined-basic" type="dropCity" className="form-control" name="dropCity" label="Dropoff City" onChange={e=>updateDropOff(e)} value={dropOff.dropCity}/>                  
                </Grid>
                <Grid item xs>
                  <TextField id="outlined-basic" type="dropState" className="form-control" name="dropState" label="Dropoff State" onChange={e=>updateDropOff(e)} value={dropOff.dropState}/>                  
                </Grid>
                <Grid item xs>
                  <TextField id="outlined-basic" type="dropZip" className="form-control" name="dropZip" label="Dropoff Zip" onChange={e=>updateDropOff(e)} value={dropOff.dropZip}/>                  
                </Grid>
              </Grid>
            </div>

          </div>
        ) : null }

        {count === 4 ? (
          <div>
            <div className="form-group">
              <MuiPickersUtilsProvider utils={DateFnsUtils}>
                <KeyboardDatePicker margin="normal" id="date-picker-dialog" label="Pickup Date" format="MM/dd/yyyy" value={pickUp.pickupDate} onChange={date => updateDate(date,0)}/>
                <KeyboardTimePicker margin="normal" id="time-picker" label="Pickup Time" value={pickUp.pickupDate} onChange={date => updateDate(date,0)}/>
              </MuiPickersUtilsProvider>
            </div>

            <div className="form-group">
              <MuiPickersUtilsProvider utils={DateFnsUtils}>
                <KeyboardDatePicker margin="normal" id="date-picker-dialog" label="Dropoff Date" format="MM/dd/yyyy" value={dropOff.dropDate} onChange={date => updateDate(date,1)}/>
                <KeyboardTimePicker margin="normal" id="time-picker" label="Dropoff Time" value={dropOff.dropDate} onChange={date => updateDate(date,1)}/>
              </MuiPickersUtilsProvider>
            </div>
          </div>
        ) : null}

        {count === 5 ? (
          <div>
            <div className="form-group">
              <TextField id="outlined-basic" type="pickupPo" className="form-control" name="pickupPo" label="Pickup Po" onChange={e=>updatePickUp(e)} value={pickUp.pickupPo}/>
            </div>

            <div className="form-group">
              <TextField id="outlined-basic" type="dropPo" className="form-control" name="dropPo" label="Dropoff Po" onChange={e=>updateDropOff(e)} value={dropOff.dropPo}/>
            </div>


          </div>
        ) : null}

        {count === 6 ? (
          <div>
            <div className="form-group">
              <TextField id="outlined-basic" type="pickupDeliverNumber" className="form-control" name="pickupDeliverNumber" label="Pickup Deliver Number" onChange={e=>updatePickUp(e)} value={pickUp.pickupDeliverNumber}/>
            </div>

            <div className="form-group">
              <TextField id="outlined-basic" type="dropDeliverNumber" className="form-control" name="dropDeliverNumber" label="Dropoff Deliver Number" onChange={e=>updateDropOff(e)} value={dropOff.dropDeliverNumber}/>
            </div>
          </div>
        ) : null}

        {count === 7 ? (
          <div>
            <div className="form-group">
              <TextField id="outlined-basic" type="pickupReference" className="form-control" name="pickupReference" label="Pickup Reference" onChange={e=>updatePickUp(e)} value={pickUp.pickupReference}/>
            </div>

            <div className="form-group">
              <TextField id="outlined-basic" type="dropReference" className="form-control" name="dropReference" label="Dropoff Reference" onChange={e=>updateDropOff(e)} value={dropOff.dropReference}/>
            </div>
          </div>
        ) : null}
        </form>
          <Grid container spacing={1} style={{marginTop: '20px'}}>
            <Grid item xs={12} style={{marginBottom: '10px'}}>
              <Button className="" type="submit" onClick={onSubmit}>Submit</Button>
            </Grid>
            <Grid item xs={6}>
              <Button className="" type="submit" onClick={handleBack} disabled={count === 1}>
                Back
              </Button>
            </Grid>
            <Grid item xs={6}>
              <Button className="" type="submit" onClick={handleNext} disabled={count > 7}>
              Next
              </Button>
            </Grid>
            {count > 7 && 
              <Grid item xs={12}>
                  <Button className="" onClick={handleNewPickDrop}>
                    New PickUp and DropOff
                  </Button>
              </Grid>
              }
          </Grid>



        </div>



        </DialogContent>



        <DialogContent>
        </DialogContent>



        <DialogActions>
        </DialogActions>
      </Dialog>
    </div>
  )
}

AddLoadForm.propTypes = {
  addLoad: PropTypes.func.isRequired
};

export default connect(null, {addLoad})(AddLoadForm)
