import React, {useState} from 'react';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import DialogTitle from '@material-ui/core/DialogTitle';
import Fab from '@material-ui/core/Fab';
import AddIcon from '@material-ui/icons/Add';

import LoadForm  from './LoadNumber';

import { makeStyles } from '@material-ui/core/styles';
import Typography from '@material-ui/core/Typography';

import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { addLoad } from '../../actions/load';

const useStyles = makeStyles((theme) => ({
  backButton: {
    marginRight: theme.spacing(1),
  },
  instructions: {
    marginTop: theme.spacing(1),
    marginBottom: theme.spacing(1),
  },
}));


const AddLoadForm = ({ addLoad }) => {
  const [text, setText] = useState('');
  const [open, setOpen] = React.useState(false);


  const handleClickOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  const [count, setCount] = React.useState(1);

  const handleNext = () => {
      setCount(prevActiveStep => prevActiveStep + 1);
  };

  const handleBack = () => {
      setCount(prevActiveStep => prevActiveStep - 1);
  };

  const[form, setForm] = useState({
    brokerage:'',
    loadNumber: '',
    shipperName:''
  });

  const {
    brokerage,
    loadNumber,
    shipperName
  } = form;



  const updateForm = e =>
    setForm({
      ...form,
      [e.target.name]: e.target.value
    });

    const onSubmit = e => {
      e.preventDefault();
      addLoad(form);
    };




  return (
    <div>

      <Fab color="primary" onClick={handleClickOpen}>
        <AddIcon />
      </Fab>

      <Dialog open={open} onClose={handleClose} aria-labelledby="form-dialog-title">

        <DialogTitle id="form-dialog-title">Subscribe</DialogTitle>

        <DialogContent>

        <div className="">
        <form className="" onSubmit ={e => onSubmit(e)}>

        { count === 1 ? (
        <div>
        <div className="form-group">
        <label> Brokerage </label>
        <input type="brokerage" className="form-control" name="brokerage" onChange={e => updateForm(e)} value={brokerage} />
        </div>

        <div className="form-group">
        <label> Load Number </label>
        <input type="loadNumber" className="form-control" name="loadNumber" onChange={e => updateForm(e)} value={loadNumber} />
        </div>
        </div>

        ) : null }

        { count === 2 ? (
        <div>
        <div className="form-group">
        <label> shipper name </label>
        <input type="shipperName" className="form-control" name="shipperName" onChange={e => updateForm(e)} value={pickup.shipperName} />
        </div>
        </div>

        ) : null }




        <button className="" type="submit">Submit</button>

        </form>
          <button className="" type="submit" onClick={handleBack} disabled={count < 2}>
            Back
          </button>

          <button className="" type="submit" onClick={handleNext} disabled={count > 2}>
          Next
          </button>
        </div>



        </DialogContent>



        <DialogContent>
        </DialogContent>



        <DialogActions>
        </DialogActions>
      </Dialog>
    </div>
  )
}

AddLoadForm.propTypes = {
  addLoad: PropTypes.func.isRequired
};

export default connect(null, {addLoad})(AddLoadForm)
