import React, { useState } from 'react';
import { Modal, Step } from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import Grid from '@material-ui/core/Grid';
import TextField from '@material-ui/core/TextField';
import { Button } from '@material-ui/core';
import IconButton from '@material-ui/core/IconButton';
import ArrowForwardIcon from '@material-ui/icons/ArrowForward';
import ArrowBackIcon from '@material-ui/icons/ArrowBack';

const useStyles = makeStyles((theme) => ({
    paper: {
        position: 'absolute',
        backgroundColor: theme.palette.background.paper,
        border: '2px solid #000',
        boxShadow: theme.shadows[5],
        padding: theme.spacing(2, 4, 3),
    },
    root: {
        flexGrow: 1,
        flexDirection: "row",
        textAlign: "center"
    },
    formControl: {
        margin: theme.spacing(1),
        minWidth: 120,
    },
    resizeDialog: {
        fontSize: 14,
    },
    modalBody: {
        width: '100%',
        minHeight: '324px'
    },
    modalFooter: {
        width: '100%',
    },
    btn_container: {
        textAlign: 'end'
    }
}));
const getModalStyle = () => {
    return {
        top: '50%',
        left: '50%',
        width: '400px',
        transform: 'translate(-50%, -50%)',
        overflowY: 'auto',
        height: '400px',
    };
};
const formInitialState = {
    from: {
        name: '',
        company_name: '',
        email: ''
    },
    to: {
        company_name: '',
        email: ''
    }
};

export default function InvoiceWizard({ load_selected, handleOnClose }) {
    const classes = useStyles();
    const [modalStyle] = useState(getModalStyle);
    const [form, setForm] = useState(formInitialState);
    const [step, setStep] = useState(1);
    const handleSubmit = (event) => {
        event.preventDefault();
    };
    const handleOnChange = (event, key) => {
        const { name, value } = event.target;
        setForm(form => ({ ...form, [key]: { ...form[key], [name]: value } }));
    };
    const changeStep = (move) => {
        const new_step = move === 'next' ? step+1 : step-1;
        let error = false;
        if (step === 1 && (!form.from.name || !form.from.company_name || !form.from.email))
            error = true;
        if (step === 2 && (!form.to.company_name || !form.to.email))
            error = true;
        if (move === 'next' && error)
            alert('Please fill the form to continue.');
        else
            setStep(new_step);
    };
    return (<Modal
    open={load_selected ? true : false}
    onClose={()=>handleOnClose()}>
        <div style={modalStyle} className={classes.paper}>
            <form onSubmit={handleSubmit}>
                <Grid container spacing={2} className={classes.root}>
                    <div className={classes.modalBody}>
                        { step !== 3 ? <Grid item xs={12}>
                            <h3>{ step === 1 ? 'From' : 'To' }</h3>
                        </Grid> : '' }
                        { step === 1 ? <Grid item xs={12}>
                            <Grid container spacing={2}>
                                <Grid item xs={3}></Grid>
                                <Grid item xs={6}>
                                    <TextField 
                                    id="outlined-basic" 
                                    variant="outlined" 
                                    label="Name"
                                    name="name"
                                    value={form[step === 1 ? 'from' : 'to'].name}
                                    onChange={(event) => handleOnChange(event, step === 1 ? 'from' : 'to')} />
                                </Grid>
                                <Grid item xs={3}></Grid>
                            </Grid>
                        </Grid> : '' }
                        { step < 3 ? <><Grid item xs={12}>
                            <Grid container spacing={2}>
                                <Grid item xs={3}></Grid>
                                <Grid item xs={6}>
                                    <TextField 
                                    id="outlined-basic" 
                                    variant="outlined" 
                                    label="Company Name"
                                    name="company_name"
                                    value={form[step === 1 ? 'from' : 'to'].company_name}
                                    onChange={(event) => handleOnChange(event, step === 1 ? 'from' : 'to')} />
                                </Grid>
                                <Grid item xs={3}></Grid>
                            </Grid>
                        </Grid>
                        <Grid item xs={12}>
                            <Grid container spacing={2}>
                                <Grid item xs={3}></Grid>
                                <Grid item xs={6}>
                                    <TextField 
                                    id="outlined-basic" 
                                    variant="outlined" 
                                    label="Email"
                                    name="email"
                                    value={form[step === 1 ? 'from' : 'to'].email}
                                    onChange={(event) => handleOnChange(event, step === 1 ? 'from' : 'to')} />
                                </Grid>
                                <Grid item xs={3}></Grid>
                            </Grid>
                        </Grid></> : '' }
                        { step === 3 && load_selected ? <Grid item xs={12}>
                            <Grid container spacing={2}>
                                <Grid item xs={4}></Grid>
                                <Grid item xs={4}></Grid>
                                <Grid item xs={4}></Grid>
                            </Grid>
                            <Grid container spacing={2}>
                                <Grid item xs={3}></Grid>
                                <Grid item xs={6}>
                                    <p>Load: { load_selected.loadNumber || '--' }</p>
                                </Grid>
                                <Grid item xs={3}></Grid>
                            </Grid>
                            <Grid container spacing={2}>
                                <Grid item xs={3}></Grid>
                                <Grid item xs={6}>
                                    <p>Broker: { load_selected.brokerage || '--' }</p>
                                </Grid>
                                <Grid item xs={3}></Grid>
                            </Grid>
                            <Grid container spacing={2}>
                                <Grid item xs={3}></Grid>
                                <Grid item xs={6}>
                                    <p>Rate: { load_selected.rate || '--' }</p>
                                </Grid>
                                <Grid item xs={3}></Grid>
                            </Grid>
                            <Grid container spacing={2}>
                                <Grid item xs={3}></Grid>
                                <Grid item xs={6}>
                                    <p style={{margin:0}}>
                                        Rate Confirmation: 
                                        { 
                                            Array.isArray(load_selected.rateConfirmation) && load_selected.rateConfirmation.length > 0 && typeof load_selected.rateConfirmation[0] !== 'string'  ? 
                                            <Button variant="contained" style={{color:'red'}}>Delete</Button> : <Button variant="contained" color="primary">Upload</Button>
                                        }
                                    </p>
                                </Grid>
                                <Grid item xs={3}></Grid>
                            </Grid>
                            <Grid container spacing={2}>
                                <Grid item xs={3}></Grid>
                                <Grid item xs={6}>
                                    <p style={{margin:0}}>
                                        Proof of Delivery: 
                                        { 
                                            Array.isArray(load_selected.proofDelivery) && load_selected.proofDelivery.length > 0 && typeof load_selected.proofDelivery[0] !== 'string'  ? 
                                            <Button variant="contained" style={{color:'red'}}>Delete</Button> : <Button variant="contained" color="primary">Upload</Button>
                                        }
                                    </p>
                                </Grid>
                                <Grid item xs={3}></Grid>
                            </Grid>
                        </Grid> : '' }
                    </div>
                    <div className={classes.modalFooter}>
                        <Grid item xs={12}>
                            <Grid container spacing={2}>
                                <Grid item xs={2}></Grid>
                                <Grid item xs={2}></Grid>
                                <Grid item xs={8} className={classes.btn_container}>
                                    {
                                        step !== 3 ?
                                        (
                                            <IconButton>
                                                { step > 1 ? <ArrowBackIcon onClick={()=>changeStep('back')}/> : '' }
                                                <ArrowForwardIcon onClick={()=>changeStep('next')}/>
                                            </IconButton>
                                        ) : ''
                                    }
                                    { step === 3 ? <div>
                                        <Button 
                                        variant="contained" 
                                        color="primary" 
                                        onClick={()=>changeStep('back')}
                                        style={{marginRight:'5px'}}>Back</Button>
                                        <Button variant="contained" color="primary">Create</Button>
                                    </div> : '' }
                                </Grid>
                            </Grid>
                        </Grid>
                    </div>
                </Grid>
            </form>
        </div>
    </Modal>);
}
