import React, { Fragment, useEffect } from "react";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { getLoads } from "../../actions/load";
import LoadItem from "./Loaditem";
import Spinner from "../layout/Spinner";
import TableRow from '@material-ui/core/TableRow';
import TableFooter from '@material-ui/core/TableFooter';
import TablePagination from '@material-ui/core/TablePagination';

const Loads = ({ getLoads, load: { loads, loading, loads_pagination, page, rowsPerPage }, driver: { drivers } }) => {
  const { total } = loads_pagination;

  useEffect(() => {
    getLoads(page, rowsPerPage);
  }, []);

  const handleChangePage = (event, newPage) => {
    getLoads(newPage, rowsPerPage);
  };

  const handleChangeRowsPerPage = (event) => {
    const limit = event.target.value;
    getLoads(0, limit);
  };

  return (
    <Fragment>
      {" "}
      {loading ? (
        <Spinner />
      ) : (
        <Fragment>

            {loads.length > 0 ? (
              loads.map((l) => <LoadItem key={l._id} load={l} loads={loads} page={page} rowsPerPage={rowsPerPage} />)
            ) : (
              <h4>No loads</h4>
            )}

            {loads.length > 0 ? (
              <TableFooter>
                <TableRow>
                  <TablePagination
                    rowsPerPageOptions={[5, 10, 15]}
                    colSpan={3}
                    count={total}
                    rowsPerPage={rowsPerPage}
                    page={page}
                    SelectProps={{
                      inputProps: { 'aria-label': 'rows per page' },
                      native: true,
                    }}
                    onChangePage={handleChangePage}
                    onChangeRowsPerPage={handleChangeRowsPerPage}
                  />
                </TableRow>
              </TableFooter>): '' }

        </Fragment>
      )}
    </Fragment>
  );
};

Loads.propTypes = {
  getLoads: PropTypes.func.isRequired,
  load: PropTypes.object.isRequired,
};

const mapStateToProps = (state) => ({
  load: state.load,
  driver: state.driver
});

export default connect(mapStateToProps, { getLoads })(Loads);
