import React, { Fragment, useEffect } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { getLoads } from '../../actions/load';
import LoadItem  from './Loaditem';
import Spinner from '../layout/Spinner';

const Loads = ({getLoads, load: { loads, loading }}) => {
  useEffect(() => {
    getLoads();
  }, [getLoads]);

  return loading ? <Spinner /> : (
    <Fragment>
    <h1 className="large text-primary">Loads</h1>
      <p className="lead">
        <i className="fas fa-user" /> Welcome to the community
      </p>
      <div className="posts">
        {loads.map((post) => (
          <LoadItem key={load._id} post={load} />
        ))}
      </div>
    </Fragment>
  );

};

Loads.propTypes = {
  getLoads: PropTypes.func.isRequired,
  load: PropTypes.object.isRequired
}

const mapStateToProps = state => ({
  load: state.load
});

export default connect(mapStateToProps, {getLoads})(Loads);
