import React, { Fragment, useEffect } from "react";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { getLoads } from "../../actions/load";
import CurrentDayLoadItem from "./CurrentDayLoadItem";
import Spinner from "../layout/Spinner";

const CurrentDayLoads = ({ getLoads, load: { loads, loading } }) => {
  useEffect(() => {
    getLoads();
  }, []);

  return (
    <Fragment>
      {" "}
      {loading ? (
        <Spinner />
      ) : (
        <Fragment>

            {loads.length > 0 ? (
              loads.map((load) => <CurrentDayLoadItem key={load._id} load={load} load_id={load._id} />)
            ) : (
              <h4>No load Today</h4>
            )}

        </Fragment>
      )}
    </Fragment>
  );
};

CurrentDayLoads.propTypes = {
  getLoads: PropTypes.func.isRequired,
  load: PropTypes.object.isRequired,
};

const mapStateToProps = (state) => ({
  load: state.load,
});

export default connect(mapStateToProps, { getLoads })(CurrentDayLoads);
