import React, { Fragment } from 'react';
import PropTypes from 'prop-types';
import Moment from 'react-moment';
import LoadPickup from './LoadPickup';
import LoadDrop from './LoadDrop';
import { connect } from 'react-redux';
import { makeStyles, withStyles, useTheme  } from '@material-ui/core/styles';

import Paper from '@material-ui/core/Paper';
import Badge from '@material-ui/core/Badge';



import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import IconButton from '@material-ui/core/IconButton';
import Typography from '@material-ui/core/Typography';
import SkipPreviousIcon from '@material-ui/icons/SkipPrevious';
import PlayArrowIcon from '@material-ui/icons/PlayArrow';
import SkipNextIcon from '@material-ui/icons/SkipNext';
import ArrowForwardRoundedIcon from '@material-ui/icons/ArrowForwardRounded';

import AirplanemodeActive from '@material-ui/icons/AirplanemodeActive';
import VerticalTicketRip from '@mui-treasury/components/rip/verticalTicket';
import { useVerticalRipStyles } from '@mui-treasury/styles/rip/vertical';

import theme from "../layout/ui/Theme";

const useStyles = makeStyles((theme) => ({

  root: {
   display: 'flex',
   minWidth: 200,
   maxWidth: 300,
   marginBottom: 10

 },
 card: {
  overflow: 'visible',
  background: 'none',
  display: 'flex',
  minWidth: 343,
  minHeight: 150,
  filter: 'drop-shadow(1px 1px 3px rgba(0, 0, 0, 0.3))',
  '& $moveLeft, $moveRight': {
    transition: '0.3s',
  },
  '&:hover': {
    '& $moveLeft': {
      transform: 'translateX(-8px)',
    },
    '& $moveRight': {
      transform: 'translateX(8px)',
    },
  },
  [breakpoints.up('sm')]: {
    minWidth: 400,
  },
},
left: {
  borderTopLeftRadius: borderRadius,
  borderBottomLeftRadius: borderRadius,
  flexBasis: '33.33%',
  display: 'flex',
  backgroundColor: '#fff',
},
media: {
  margin: 'auto',
  width: 80,
  height: 80,
  borderRadius: '50%',
},
right: {
  borderTopRightRadius: borderRadius,
  borderBottomRightRadius: borderRadius,
  flex: 1,
  padding: 12,
  display: 'flex',
  alignItems: 'center',
  textAlign: 'center',
  backgroundColor: lightColor,
},
label: {
  padding: '0 8px',
},
heading: {
  fontSize: 24,
  fontWeight: 'bold',
  margin: 0,
  marginBottom: 4,
},
subheader: {
  fontSize: 12,
  margin: 0,
  color: palette.text.secondary,
},
path: {
  flex: 1,
  flexBasis: 72,
  padding: '0 4px',
},
line: {
  position: 'relative',
  margin: '20px 0 16px',
  borderBottom: '1px dashed rgba(0,0,0,0.38)',
},
plane: {
  position: 'absolute',
  display: 'inline-block',
  padding: '0 4px',
  fontSize: 32,
  backgroundColor: lightColor,
  top: '50%',
  left: '50%',
  transform: 'translate(-50%, -50%) rotate(90deg)',
},
flight: {
  fontSize: 14,
  lineHeight: '24px',
  minWidth: 48,
  padding: '0 8px',
  borderRadius: 40,
  backgroundColor: '#bed0f5',
  color: mainColor,
  display: 'block',
},
moveLeft: {},
moveRight: {},

}));


// <h5>
// {pickup.length > 0 ? (<Fragment>
//   {pickup.map(pickup => (
//     <LoadPickup key={pickup._id} pickup={pickup} />
//   ))}
// </Fragment>) : (<h4>No pickups listed</h4>)}
// </h5>

// <h5>
// {drop.length > 0 ? (<Fragment>
//   {drop.map(drop => (
//     <LoadDrop key={drop._id} drop={drop} />
//   ))}
// </Fragment>) : (<h4>No drops listed</h4>)}
// </h5>


const CurrentDayLoadItem = ({
  load: {
    user: { _id, name },
    brokerage,
    loadNumber,
    pickup,
    drop,
  },
}) => {
  const classes = useStyles();

  const borderRadius = 12;

  const pickupState = pickup.map((pickup) => pickup.pickupState);
  const pickupCity = pickup.map((pickup) => pickup.pickupCity);
  const pickupDate = pickup.map((pickup) => pickup.pickupDate);

  const dropState = drop.map((drop) => drop.dropState);
  const dropCity = drop.map((drop) => drop.dropCity);
  const dropDate = drop.map((drop) => drop.dropDate);

  const pickupStateFirst = pickupState[0];
  const pickupCityFirst = pickupCity[0];
  const pickDateFirst = pickupDate[0];

  const dropStateFirst = dropState[0];
  const dropCityFirst = dropCity[0];
  const dropDateFirst = dropDate[0];


  return (
    <Card className={styles.card} elevation={0}>
      <div className={cx(styles.left, styles.moveLeft)}>
        <CardMedia
          className={styles.media}
          image={
            'https://dejpknyizje2n.cloudfront.net/marketplace/products/yin-yang-two-fighting-dragons-sticker-1538772130.3390164.png'
          }
        />
      </div>
      <VerticalTicketRip
        classes={{
          ...ripStyles,
          left: cx(ripStyles.left, styles.moveLeft),
          right: cx(ripStyles.right, styles.moveRight),
        }}
      />
      <div className={cx(styles.right, styles.moveRight)}>
        <div className={styles.label}>
          <h2 className={styles.heading}>BEK</h2>
          <p className={styles.subheader}>Beijing China</p>
        </div>
        <div className={styles.path}>
          <div className={styles.line}>
            <AirplanemodeActive className={styles.plane} />
          </div>
          <span className={styles.flight}>AB256</span>
        </div>
        <div className={styles.label}>
          <h2 className={styles.heading}>DMK</h2>
          <p className={styles.subheader}>Don Meaung</p>
        </div>
      </div>
    </Card>


  );
};




CurrentDayLoadItem.propTypes = {
  load: PropTypes.object.isRequired,
};




export default CurrentDayLoadItem;
