import {
  GET_LOADS,
  GET_LOAD,
  LOAD_ERROR,
  ADD_LOAD,
  PATCH_PICKUP,
  PICKUP_ERROR,
  PATCH_DROP,
  DROP_ERROR,
} from '../actions/types';

const initialState = {
  loads: [],
  load: null,
  loading: true,
  error: {}
};

export default function(state = initialState, action) {
  const { type, payload } = action;

  switch(type) {
    case GET_LOADS:
      return {
        ...state,
        loads: payload,
        loading: false
      };
      case GET_LOAD:
        return {
          ...state,
          load: payload,
          loading: false
        };
      case ADD_LOAD:
      return {
        ...state,
        loads: [...state.loads, payload],
        loading: false
      };
      case LOAD_ERROR:
        return {
          ...state,
          error: payload,
          loading: false
      };
      case PATCH_PICKUP:
        return{
          ...state,
          load: payload,
          loading: false,
        }
      case PICKUP_ERROR:
        return{
          ...state,
          error:payload,
          loading: false,
        };
      case PATCH_DROP:
        return{
          ...state,
          load: payload,
          loading:false,
        };
      case DROP_ERROR:
        return{
          ...state,
          load: payload,
          loading:false,
        };
      default:
        return state;
  }

}
