import React, { useState, useRef } from 'react';
import { Modal, Step } from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import Grid from '@material-ui/core/Grid';
import TextField from '@material-ui/core/TextField';
import { Button } from '@material-ui/core';
import IconButton from '@material-ui/core/IconButton';
import ArrowForwardIcon from '@material-ui/icons/ArrowForward';
import ArrowBackIcon from '@material-ui/icons/ArrowBack';
import { useDispatch } from 'react-redux';
import { deleteLoadDocument, uploadLoadDocument, generateInvoice } from '../../actions/load';
import { Page, Text, View, Document, StyleSheet, PDFDownloadLink, pdf } from '@react-pdf/renderer';
import { saveAs } from "file-saver";

const styles = StyleSheet.create({
    page: {
      flexDirection: 'column',
      backgroundColor: '#E4E4E4'
    },
    section: {
      margin: 5,
      padding: 5,
      textAlign: 'center'
    }
});

const generatePDFDocument = async ({ from, to, load_number, broker, rate }) => {
    const blob = await pdf(
        <Document>
            <Page size="A4" style={styles.page}>
                <View style={styles.section}>
                    <Text>From</Text>
                    <Text>Name: {from.name}</Text>
                    <Text>Company Name: {from.company_name}</Text>
                    <Text>Email: {from.email}</Text>
                </View>
                <View style={styles.section}>
                    <Text>To</Text>
                    <Text>Company Name: {to.company_name}</Text>
                    <Text>Email: {to.email}</Text>
                </View>
                <View style={styles.section}>
                    <Text>Load</Text>
                    <Text>{load_number}</Text>
                </View>
                <View></View>
                <View style={styles.section}>
                    <Text>Broker</Text>
                    <Text>{broker ? broker : '--'}</Text>
                </View>
                <View style={styles.section}>
                    <Text>Rate</Text>
                    <Text>{rate ? rate: '--'}</Text>
                </View>
            </Page>
        </Document>
    ).toBlob();

    saveAs(blob, "Invoice");
};


const useStyles = makeStyles((theme) => ({
    paper: {
        position: 'absolute',
        backgroundColor: theme.palette.background.paper,
        border: '2px solid #000',
        boxShadow: theme.shadows[5],
        padding: theme.spacing(2, 4, 3),
    },
    root: {
        flexGrow: 1,
        flexDirection: "row",
        textAlign: "center"
    },
    formControl: {
        margin: theme.spacing(1),
        minWidth: 120,
    },
    resizeDialog: {
        fontSize: 14,
    },
    modalBody: {
        width: '100%',
        minHeight: '324px'
    },
    modalFooter: {
        width: '100%',
    },
    btn_container: {
        textAlign: 'end'
    }
}));
const getModalStyle = () => {
    return {
        top: '50%',
        left: '50%',
        width: '400px',
        transform: 'translate(-50%, -50%)',
        overflowY: 'auto',
        height: '400px',
    };
};
const formInitialState = {
    from: {
        name: '',
        company_name: '',
        email: ''
    },
    to: {
        company_name: '',
        email: ''
    }
};

export default function InvoiceWizard({ load_selected, handleOnClose, deleteDoc, updateDoc }) {
    const classes = useStyles();
    const dispatch = useDispatch();
    const [modalStyle] = useState(getModalStyle);
    const [form, setForm] = useState(formInitialState);
    const [step, setStep] = useState(1);
    const rcfr = useRef();
    const pdfr = useRef();
    const handleSubmit = (event = null) => {
        if (event)
            event.preventDefault();
        dispatch(generateInvoice(load_selected._id, form));
        generatePDFDocument({
            from: form.from, 
            to: form.to, 
            load_number: load_selected.loadNumber,
            broker: load_selected.brokerage,
            rate: load_selected.rate 
        });
        setTimeout(() => {
            handleOnClose();
        }, 2000);
    };
    const handleOnChange = (event, key) => {
        const { name, value } = event.target;
        setForm(form => ({ ...form, [key]: { ...form[key], [name]: value } }));
    };
    const changeStep = (move) => {
        const new_step = move === 'next' ? step+1 : step-1;
        let error = false;
        let errorMsg = '';
        if ((step === 1 || step === 2) && 
        ((form.from.email && !validateEmail(form.from.email)) || (form.to.email && !validateEmail(form.to.email)))) {
            error = true;
            errorMsg = 'Invalid Email';
        }
        if (step === 1 && (!form.from.name || !form.from.company_name || !form.from.email)) {
            error = true;
            errorMsg = 'Please fill the form to continue.';
        }
        if (step === 2 && (!form.to.company_name || !form.to.email)) {
            error = true;
            errorMsg = 'Please fill the form to continue.';
        }
        if (move === 'next' && error)
            alert(errorMsg);
        else
            setStep(new_step);
    };
    const validateEmail = email => {
        const re = /^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
        return re.test(String(email).toLowerCase());
    }
    const deleteDocument = doc_type => {
        dispatch(deleteLoadDocument(load_selected._id, doc_type, ''));
        // remove timer and get actual state of invoice to show the upload button instead of delete button on delete success
        setTimeout(() => {deleteDoc(doc_type)}, 2000);
    };
    const uploadDocument = (doc_type, documents) => {
        const file_data = [];
        for (let file of documents) {
            const { filename } = file;
            file_data.push({
                name: filename,
                date: Date.now()
            });
        }
        dispatch(uploadLoadDocument(load_selected._id, doc_type, documents));
        setTimeout(() => {updateDoc(doc_type, file_data)}, 2000);
    }
    return (<Modal
    open={load_selected ? true : false}
    onClose={()=>{
        handleOnClose();
        setForm(formInitialState);
        setStep(1);
    }}>
        <div style={modalStyle} className={classes.paper}>
            <form onSubmit={handleSubmit}>
                <Grid container spacing={2} className={classes.root}>
                    <div className={classes.modalBody}>
                        { step !== 3 ? <Grid item xs={12}>
                            <h3>{ step === 1 ? 'From' : 'To' }</h3>
                        </Grid> : '' }
                        { step === 1 ? <Grid item xs={12}>
                            <Grid container spacing={2}>
                                <Grid item xs={3}></Grid>
                                <Grid item xs={6}>
                                    <TextField 
                                    id="outlined-basic" 
                                    variant="outlined" 
                                    label="Name"
                                    name="name"
                                    value={form[step === 1 ? 'from' : 'to'].name}
                                    onChange={(event) => handleOnChange(event, step === 1 ? 'from' : 'to')} />
                                </Grid>
                                <Grid item xs={3}></Grid>
                            </Grid>
                        </Grid> : '' }
                        { step < 3 ? <><Grid item xs={12}>
                            <Grid container spacing={2}>
                                <Grid item xs={3}></Grid>
                                <Grid item xs={6}>
                                    <TextField 
                                    id="outlined-basic" 
                                    variant="outlined" 
                                    label="Company Name"
                                    name="company_name"
                                    value={form[step === 1 ? 'from' : 'to'].company_name}
                                    onChange={(event) => handleOnChange(event, step === 1 ? 'from' : 'to')} />
                                </Grid>
                                <Grid item xs={3}></Grid>
                            </Grid>
                        </Grid>
                        <Grid item xs={12}>
                            <Grid container spacing={2}>
                                <Grid item xs={3}></Grid>
                                <Grid item xs={6}>
                                    <TextField 
                                    id="outlined-basic" 
                                    variant="outlined" 
                                    label="Email"
                                    name="email"
                                    value={form[step === 1 ? 'from' : 'to'].email}
                                    onChange={(event) => handleOnChange(event, step === 1 ? 'from' : 'to')} />
                                </Grid>
                                <Grid item xs={3}></Grid>
                            </Grid>
                        </Grid></> : '' }
                        { step === 3 && load_selected ? <Grid item xs={12}>
                            <Grid container spacing={2}>
                                <Grid item xs={4}></Grid>
                                <Grid item xs={4}></Grid>
                                <Grid item xs={4}></Grid>
                            </Grid>
                            <Grid container spacing={2}>
                                <Grid item xs={3}></Grid>
                                <Grid item xs={6}>
                                    <p>Load: { load_selected.loadNumber || '--' }</p>
                                </Grid>
                                <Grid item xs={3}></Grid>
                            </Grid>
                            <Grid container spacing={2}>
                                <Grid item xs={3}></Grid>
                                <Grid item xs={6}>
                                    <p>Broker: { load_selected.brokerage || '--' }</p>
                                </Grid>
                                <Grid item xs={3}></Grid>
                            </Grid>
                            <Grid container spacing={2}>
                                <Grid item xs={3}></Grid>
                                <Grid item xs={6}>
                                    <p>Rate: { load_selected.rate || '--' }</p>
                                </Grid>
                                <Grid item xs={3}></Grid>
                            </Grid>
                            <Grid container spacing={2}>
                                <Grid item xs={3}></Grid>
                                <Grid item xs={6}>
                                    <input
                                    ref={rcfr} 
                                    onChange={event=> uploadDocument('rateConfirmation', event.target.files)}
                                    type="file" 
                                    name="rateConfirmation" 
                                    multiple 
                                    style={{opacity: 0}}/>
                                    <p style={{margin:0}}>
                                        Rate Confirmation: 
                                        { 
                                            Array.isArray(load_selected.rateConfirmation) &&
                                            load_selected.rateConfirmation.length > 0 && 
                                            typeof load_selected.rateConfirmation[0] !== 'string' ? 
                                            <Button 
                                            onClick={()=>deleteDocument('rateConfirmation')} 
                                            variant="contained" 
                                            style={{backgroundColor:'red',color:'#ffffff'}}>
                                            Delete
                                            </Button> : 
                                            <Button 
                                            onClick={()=>rcfr.current.click()} 
                                            variant="contained" color="primary">
                                            Upload
                                            </Button>
                                        }
                                    </p>
                                </Grid>
                                <Grid item xs={3}></Grid>
                            </Grid>
                            <Grid container spacing={2}>
                                <Grid item xs={3}></Grid>
                                <Grid item xs={6}>
                                    <input
                                    ref={pdfr} 
                                    onChange={event=> uploadDocument('proofDelivery', event.target.files)}
                                    type="file" 
                                    name="proofDelivery" 
                                    multiple 
                                    style={{opacity: 0}}/>
                                    <p style={{margin:0}}>
                                        Proof of Delivery: 
                                        { 
                                            Array.isArray(load_selected.proofDelivery) && 
                                            load_selected.proofDelivery.length > 0 && 
                                            typeof load_selected.proofDelivery[0] !== 'string' ? 
                                            <Button 
                                            onClick={()=>deleteDocument('proofDelivery')} 
                                            variant="contained" 
                                            style={{backgroundColor:'red',color:'#ffffff'}}>
                                            Delete
                                            </Button> : 
                                            <Button 
                                            onClick={()=>pdfr.current.click()} 
                                            variant="contained" 
                                            color="primary">
                                            Upload
                                            </Button>
                                        }
                                    </p>
                                </Grid>
                                <Grid item xs={3}></Grid>
                            </Grid>
                        </Grid> : '' }
                    </div>
                    <div className={classes.modalFooter}>
                        <Grid item xs={12}>
                            <Grid container spacing={2}>
                                <Grid item xs={2}></Grid>
                                <Grid item xs={2}></Grid>
                                <Grid item xs={8} className={classes.btn_container}>
                                    {
                                        step !== 3 ?
                                        (
                                            <IconButton>
                                                { step > 1 ? <ArrowBackIcon onClick={()=>changeStep('back')}/> : '' }
                                                <ArrowForwardIcon onClick={()=>changeStep('next')}/>
                                            </IconButton>
                                        ) : ''
                                    }
                                    { step === 3 ? <div>
                                        <Button 
                                        variant="contained" 
                                        color="primary" 
                                        onClick={()=>changeStep('back')}
                                        style={{marginRight:'5px'}}>Back</Button>
                                        <Button variant="contained" color="primary" onClick={()=>handleSubmit()}>Create</Button>
                                    </div> : '' }
                                </Grid>
                            </Grid>
                        </Grid>
                    </div>
                </Grid>
            </form>
        </div>
    </Modal>);
}
