import React, {useState} from 'react';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import DialogTitle from '@material-ui/core/DialogTitle';
import Fab from '@material-ui/core/Fab';
import AddIcon from '@material-ui/icons/Add';

import LoadForm  from './LoadNumber';

import { makeStyles } from '@material-ui/core/styles';
import Stepper from '@material-ui/core/Stepper';
import Step from '@material-ui/core/Step';
import StepLabel from '@material-ui/core/StepLabel';
import Typography from '@material-ui/core/Typography';

import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { addLoad } from '../../actions/load';

const useStyles = makeStyles((theme) => ({
  backButton: {
    marginRight: theme.spacing(1),
  },
  instructions: {
    marginTop: theme.spacing(1),
    marginBottom: theme.spacing(1),
  },
}));

const StepperForm = ({}) => {

  const [text, setText] = useState('');
  const [open, setOpen] = React.useState(false);


  const handleClickOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  function getSteps() {
    return ['Load Details', 'Load Pickup Details', 'Load Drop Details']
  }

  function getStepContent(stepIndex) {
    switch (stepIndex) {
      case 0:
        return 'hello1';
      case 1:
        return 'hello2';
      case 2:
        return 'hello3';
      default:
        return 'Done';
    }
  }

  const classes = useStyles();
  const [activeStep, setActiveStep] = React.useState(0);
  const steps = getSteps();

  const handleNext = () => {
    setActiveStep((prevActiveStep) => prevActiveStep + 1);
  };

  const handleBack = () => {
    setActiveStep((prevActiveStep) => prevActiveStep - 1);
  };

  const handleReset = () => {
    setActiveStep(0);
  };

  return (
    <div>

    <Fab color="primary" onClick={handleClickOpen}>
      <AddIcon />
    </Fab>

    <Dialog open={open} onClose={handleClose} aria-labelledby="form-dialog-title">

    <DialogContent>
    <Stepper activeStep={activeStep} alternativeLabel>
    {steps.map((label) => (
      <Step key={label}>
        <StepLabel>{label}</StepLabel>
      </Step>
    ))}
  </Stepper>
    </DialogContent>

    <DialogActions>
    <div>
    {activeStep === steps.length ? (
      <div>
        <Typography className={classes.instructions}>Load Added</Typography>
        <Button onClick={handleReset}>Add another Load</Button>
      </div>
    ) : (
      <div>

        <div>
          <Button
            disabled={activeStep === 0}
            onClick={handleBack}
            className={classes.backButton}
          >
            Back
          </Button>
          <Button variant="contained" color="primary" onClick={handleNext}>
            {activeStep === steps.length - 1 ? 'Submit' : 'Next'}
          </Button>
        </div>
      </div>
    )}
  </div>
    </DialogActions>

    </Dialog>
    </div>
  )

}

StepperForm.propTypes = {

};

export default connect(null, {})(StepperForm)
