import React, { Fragment, useEffect } from 'react';
import PropTypes from 'prop-types';
import AppBar from '@material-ui/core/AppBar';
import CssBaseline from '@material-ui/core/CssBaseline';
import Divider from '@material-ui/core/Divider';
import Drawer from '@material-ui/core/Drawer';
import Hidden from '@material-ui/core/Hidden';
import IconButton from '@material-ui/core/IconButton';
import InboxIcon from '@material-ui/icons/MoveToInbox';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemText from '@material-ui/core/ListItemText';
import MailIcon from '@material-ui/icons/Mail';
import MenuIcon from '@material-ui/icons/Menu';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import Button from '@material-ui/core/Button';
import { makeStyles, useTheme } from '@material-ui/core/styles';
import Background from './ProfileBackground.svg';
import Grid from "@material-ui/core/Grid";
import { Loadcard } from '../loadcard/Loadcard.js';
import { Loadlistbar } from '../loadbar/Loadlistbar.js';
import DraftsIcon from '@material-ui/icons/Drafts';
import { Link } from 'react-router-dom';


import AccountCircle from '@material-ui/icons/AccountCircle';
import Switch from '@material-ui/core/Switch';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import FormGroup from '@material-ui/core/FormGroup';
import MenuItem from '@material-ui/core/MenuItem';
import Menu from '@material-ui/core/Menu';

import { CircularIndeterminate } from  '../layout/Spinner.js';
import Avatar from '@material-ui/core/Avatar';


import { connect } from "react-redux";
import { logout } from "../../actions/auth";
import { getCurrentProfile } from "../../actions/profile";



import theme from "../layout/ui/Theme";

const drawerWidth = 240;

const useStyles = makeStyles((theme) => ({
  root: {
    display: 'flex',
  },
  profile: {
    height: "19.82em",
    backgroundImage: `url(${Background})`,
    backgroundRepeat: 'no-repeat',
  },
  drawer: {
    [theme.breakpoints.up('sm')]: {
      width: drawerWidth,
      flexShrink: 0,
    },
  },
  appBar: {
    [theme.breakpoints.up('sm')]: {
      width: `calc(100% - ${drawerWidth}px)`,
      marginLeft: drawerWidth,
    },
    [theme.breakpoints.down("xs")]: {
      color:"#1891FC"
    },
    },
  tab: {
    ...theme.typography.tab,
  },
  menuButton: {
    marginRight: theme.spacing(2),
    [theme.breakpoints.up('sm')]: {
      display: 'none',
    },
  },
  // necessary for content to be below app bar
  toolbar: theme.mixins.toolbar,
  drawerPaper: {
    width: drawerWidth,
  },
  details: {
    display: 'flex',
    flexDirection: 'column',
  },
  content: {
    flex: '1 0 auto',
    padding: theme.spacing(3),
  },
  logout: {
    marginLeft: "15px"
  },

  loads: {
    marginLeft: "0px"
  },
  loadboard: {
    backgroundColor: "#808080",
  },
  loadcard: {

  },
  accountcircle: {
    backgroundColor: "black",

  },
  avatar: {
    marginTop: 10,
    width: theme.spacing(7),
    height: theme.spacing(7),
  },
  username: {
    marginTop: 5,
    color: "#FFFFFF"
  }
}));

const Dashboard = ({
  auth: { isAuthenticated, user },
  logout,
  container,
  getCurrentProfile, profile: { profile, loading }}) => {

  useEffect(() => {
    getCurrentProfile();
  }, []);

  const [spacing, setSpacing] = React.useState(2);
  const classes = useStyles();
  const theme = useTheme();
  const [mobileOpen, setMobileOpen] = React.useState(false);

  const [anchorEl, setAnchorEl] = React.useState(null);
  const open = Boolean(anchorEl);



 const handleMenu = (event) => {
   setAnchorEl(event.currentTarget);
 };

 const handleClose = () => {
   setAnchorEl(null);
 };


  const handleDrawerToggle = () => {
    setMobileOpen(!mobileOpen);
  };

  const drawer = (
    <div>
      <div className={classes.profile}>
      <div className={classes.toolbar} />
      <Grid contianer
      spacing={24}
      justify="center"
      style={{ minHeight: '100vh', maxWidth: '100%' }}
      >
      <Grid item align="center">
      <Avatar className={classes.avatar}>H</Avatar>
      <Typography className={classes.username} gutterBottom>
        {user.name}
      </Typography>
      </Grid>
      </Grid>
      </div>
      <List>

      <ListItem button component={Link} to="/loads">
        <ListItemIcon>
          <InboxIcon />
        </ListItemIcon>
        <ListItemText primary="Loads" />
      </ListItem>

      <ListItem button>
        <ListItemIcon>
          <InboxIcon />
        </ListItemIcon>
        <ListItemText primary="Load Status" />
      </ListItem>

      <ListItem button>
        <ListItemIcon>
          <InboxIcon />
        </ListItemIcon>
        <ListItemText primary="Drivers" />
      </ListItem>

      <ListItem button>
        <ListItemIcon>
          <DraftsIcon />
        </ListItemIcon>
        <ListItemText onClick={logout} primary="Logout" />
      </ListItem>

      <ListItem button component={Link} to="/edit-profile">
        <ListItemIcon>
          <DraftsIcon />
        </ListItemIcon>
        <ListItemText primary="Account" />
      </ListItem>

    </List>
    </div>
  );

  return (
      <div className={classes.root}>
        <CssBaseline />
        <AppBar position="fixed" elevation={0} color="secondary" className={classes.appBar}>
          <Toolbar>
            <IconButton
              color="inherit"
              aria-label="open drawer"
              edge="start"
              onClick={handleDrawerToggle}
              className={classes.menuButton}
            >
              <MenuIcon />
            </IconButton>
            <Typography className={classes.tab}>
              freightdok.
            </Typography>
            </Toolbar>
          </AppBar>




        <nav className={classes.drawer} aria-label="mailbox folders">
          {/* The implementation can be swapped with js to avoid SEO duplication of links. */}
          <Hidden smUp implementation="css">
            <Drawer
              container={container}
              variant="temporary"
              anchor={theme.direction === 'rtl' ? 'right' : 'left'}
              open={mobileOpen}
              onClose={handleDrawerToggle}
              classes={{
                paper: classes.drawerPaper,
              }}
              ModalProps={{
                keepMounted: true, // Better open performance on mobile.
              }}
            >
              {drawer}
            </Drawer>
          </Hidden>
          <Hidden xsDown implementation="css">
            <Drawer
              classes={{
                paper: classes.drawerPaper,
              }}
              variant="permanent"
              open
            >
              {drawer}
            </Drawer>
          </Hidden>
        </nav>

        <Grid container className={classes.loads}>
        <Grid item xs={8}>
        <main className={classes.content}>
        <div className={classes.toolbar} />
        <Loadlistbar />
        </main>
        </Grid>


        <Grid item xs={4}>
        <main className={classes.content}>
        <div className={classes.toolbar} />
        <Loadcard className={classes.loadcard}> </Loadcard>
        </main>
        </Grid>

        </Grid>

      </div>
    );
  }

Dashboard.propTypes ={
  logout: PropTypes.func.isRequired,
  auth: PropTypes.object.isRequired,
  getCurrentProfile: PropTypes.func.isRequired,
  profile: PropTypes.object.isRequired
};

const mapStateToProps = state => ({
  auth: state.auth,
  profile: state.profile

});

export default connect(mapStateToProps, {logout, getCurrentProfile} )(Dashboard);
